   592→// ============================================================================
   593→// COMBINED PREVIEW (Silences + Takes with colored markers)
   594→// ============================================================================
   595→/**
   596→ * Show combined preview with:
   597→ * - Red markers: silences safe to remove
   598→ * - Yellow markers: silences protected by takes (won't be removed)
   599→ * - Blue markers: takes (for reference)
   600→ */
   601→function showCombinedPreview(safeToRemove, protectedSilences, takes) {
   602→  // Update global state
   603→  safeSilences = safeToRemove;
   604→  previewSilences = [...safeToRemove, ...protectedSilences];
   605→  previewTakes = takes;
   606→  selectedSilenceIndices = new Set(safeToRemove.map((_, i) => i));
   607→
   608→  // Hide other views
   609→  hideAllViews();
   610→
   611→  // Update summary counts
   612→  if (ui.silenceCountDisplay) ui.silenceCountDisplay.textContent = safeToRemove.length;
   613→  if (ui.takeCountDisplay) ui.takeCountDisplay.textContent = takes.length;
   614→  if (ui.selectedCount) ui.selectedCount.textContent = safeToRemove.length;
   615→
   616→  // Build combined items list (sorted by time)
   617→  const items = [
   618→    ...safeToRemove.map((s, i) => ({ type: 'silence', index: i, start: s.start, end: s.end, data: s })),
   619→    ...protectedSilences.map(s => ({ type: 'protected', start: s.start, end: s.end, data: s })),
   620→    ...takes.map(t => ({ type: 'take', start: t.startTime || t.start, end: t.endTime || t.end, data: t }))
   621→  ].sort((a, b) => a.start - b.start);
   622→
   623→  // Build preview list using DocumentFragment
   624→  const fragment = document.createDocumentFragment();
   625→
   626→  items.forEach((item) => {
   627→    const duration = item.end - item.start;
   628→    const div = document.createElement('div');
   629→
   630→    if (item.type === 'silence') {
   631→      // Safe to remove - red marker with checkbox
   632→      div.className = 'preview-item silence-marker';
   633→      div.dataset.index = item.index;
   634→      div.innerHTML = `
   635→        <input type="checkbox" class="preview-item-check" checked data-index="${item.index}">
   636→        <div class="preview-item-info">
   637→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   638→          <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
   639→        </div>
   640→        <button class="preview-item-seek" data-time="${item.start}" title="Seek to this silence">&gt;</button>
   641→      `;
   642→    } else if (item.type === 'protected') {
   643→      // Protected by take - yellow marker, no checkbox
   644→      div.className = 'preview-item protected-silence';
   645→      div.innerHTML = `
   646→        <div style="width: 20px; margin-right: 10px;"></div>
   647→        <div class="preview-item-info">
   648→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   649→          <div class="preview-item-duration">${duration.toFixed(2)}s (overlaps speech)</div>
   650→        </div>
   651→        <button class="preview-item-seek" data-time="${item.start}" title="Seek">&gt;</button>
   652→      `;
   653→    } else if (item.type === 'take') {
   654→      // Take (speech segment) - blue marker, no checkbox
   655→      div.className = 'preview-item take-marker';
   656→      const textPreview = item.data.text?.substring(0, 40) || 'Speech segment';
   657→      div.innerHTML = `
   658→        <div style="width: 20px; margin-right: 10px;"></div>
   659→        <div class="preview-item-info">
   660→          <div class="preview-item-time">${formatTime(item.start)} - ${formatTime(item.end)}</div>
   661→          <div class="preview-item-duration">${textPreview}${item.data.text?.length > 40 ? '...' : ''}</div>
   662→        </div>
   663→        <button class="preview-item-seek" data-time="${item.start}" title="Seek to take">&gt;</button>
   664→      `;
   665→    }
   666→
   667→    fragment.appendChild(div);
   668→  });
   669→
   670→  if (ui.previewList) {
   671→    ui.previewList.textContent = '';
   672→    ui.previewList.appendChild(fragment);
   673→  }
   674→
   675→  // Reset UI state
   676→  if (ui.selectAllSilences) ui.selectAllSilences.checked = true;
   677→  updateApplyButton();
   678→
   679→  // Show combined preview section
   680→  if (ui.combinedPreview) ui.combinedPreview.style.display = 'block';
   681→}
