   815→  selectedSilenceIndices = newSelection;
   816→
   817→  // Update DOM
   818→  document.querySelectorAll('.preview-item.silence-marker .preview-item-check').forEach((checkbox) => {
   819→    const index = parseInt(checkbox.dataset.index);
   820→    const isSelected = selectedSilenceIndices.has(index);
