  1102→/**
  1103→ * Find the first media item in the project bin (recursively)
  1104→ * @param {Object} item - ProjectItem (usually rootItem)
  1105→ * @returns {Promise<Object|null>} First media item found or null
  1106→ */
  1107→async function findFirstMediaItem(item) {
  1108→  if (!item) return null;
  1109→
  1110→  // Fix: Use local Constant instead of app.Constants.ProjectItemType
  1111→  if (item.type === ProjectItemType.CLIP) {
  1112→    return item;
  1113→  }
  1114→
  1115→  // Search Children (Bin or Root)
  1116→  // Note: item.children is a collection, check numItems
  1117→  if (item.children && item.children.numItems > 0) {
  1118→    for (let i = 0; i < item.children.numItems; i++) {
  1119→      const child = item.children[i];
  1120→      const found = await findFirstMediaItem(child);
  1121→      if (found) return found;
  1122→    }
  1123→  }
  1124→
  1125→  return null;
  1126→}
