   220→      // Step 2: Get settings
   221→      const sensitivity = parseInt(ui.sensitivitySlider?.value || 50);
   222→      const useIsolated = ui.sourceIsolated?.checked ?? false;
   223→      const detectTakes = ui.enableTakesDetection?.checked ?? true;
   224→
   225→      // Step 3: Vocal isolation if needed
   226→      let audioPath = WAV_PATH;
   227→      if (useIsolated) {
   228→        // Check isolation access before proceeding
   229→        const credits = getCurrentCredits();
   230→        if (!credits || !credits.hasIsolationAccess) {
   231→          throw new Error('Vocal isolation requires Pro or Team tier. Upgrade to access this feature.');
   232→        }
   233→
   234→        // Show overage warning if applicable
   235→        const isolationMins = credits.isolationHoursRemaining * 60;
   236→        if (isolationMins <= 0) {
   237→          const overageRate = credits.isolationOverageRate || 0.08;
   238→          showProgress(`Isolating vocals (overage: $${overageRate}/min)...`);
   239→        } else {
   240→          showProgress(`Isolating vocals (${isolationMins.toFixed(0)} min remaining)...`);
   241→        }
   242→
   243→        const isolateResult = await isolateVocals();
   244→        if (isolateResult.success) {
   245→          audioPath = isolateResult.outputPath;
   246→          // Update credits display after isolation
   247→          await refreshCredits();
   248→        } else {
   249→          throw new Error(isolateResult.error || 'Vocal isolation failed');
   250→        }
   251→      }
   252→
   253→      // Step 4: Run detection (parallel if takes enabled)
   254→      const params = mapSensitivity(sensitivity);
   255→      let silences = [];
   256→      let takes = [];
   257→
   258→      if (detectTakes) {
   259→        // Parallel detection for performance
   260→        showProgress('Detecting silences and analyzing takes...');
   261→        const [silenceResult, transcriptResult] = await Promise.all([
   262→          detectSilences(audioPath, params),
   263→          transcribeAudio()
   264→        ]);
   265→
   266→        silences = silenceResult;
   267→
   268→        // Extract takes from transcript result
   269→        if (transcriptResult.success && transcriptResult.takes) {
   270→          if (transcriptResult.takes.takes) {
   271→            takes = transcriptResult.takes.takes;
   272→          } else if (Array.isArray(transcriptResult.takes)) {
   273→            takes = transcriptResult.takes;
   274→          }
   275→        }
   276→
   277→        // Store transcript for reference
   278→        window.currentTranscript = transcriptResult.transcript;
   279→      } else {
   280→        // Just detect silences
   281→        showProgress('Detecting silences...');
   282→        silences = await detectSilences(audioPath, params);
   283→      }
   284→
   285→      if (silences.length === 0) {
   286→        showEmptyState();
   287→        setStatus('No silences detected. Try increasing sensitivity.');
   288→        return;
   289→      }
   290→
   291→      // Step 5: Filter silences by takes (protect speech)
   292→      const filtered = filterSilencesByTakes(silences, takes);
   293→
   294→      // Step 6: Add timeline markers (clear old ones first)
   295→      showProgress('Adding timeline markers...');
   296→      await clearSpliceMarkers();
