   298→
   299→      // Step 7: Show combined preview
   300→      showCombinedPreview(filtered.safe, filtered.protected, takes);
   301→
   302→      const markerInfo = markerResult.added > 0 ? ` (${markerResult.added} markers added)` : '';
   303→      const statusMsg = takes.length > 0
   304→        ? `Found ${silences.length} silences, ${filtered.protected.length} protected by ${takes.length} takes${markerInfo}`
   305→        : `Found ${silences.length} silence(s) - review and apply${markerInfo}`;
   306→      setStatus(statusMsg);
