   164→// ============================================================================
   165→// FETCH UTILITIES
   166→// ============================================================================
   167→
   168→/**
   169→ * Fetch with timeout support
   170→ * @param {string} url - URL to fetch
   171→ * @param {object} options - Fetch options
   172→ * @param {number} timeout - Timeout in ms
   173→ */
   174→async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
   175→    const controller = new AbortController();
   176→    const timeoutId = setTimeout(() => controller.abort(), timeout);
   177→
   178→    try {
   179→        const response = await fetch(url, {
   180→            ...options,
   181→            signal: controller.signal
   182→        });
   183→        clearTimeout(timeoutId);
   184→        return response;
   185→    } catch (error) {
   186→        clearTimeout(timeoutId);
   187→        if (error.name === 'AbortError') {
   188→            throw new Error(`Request timeout after ${timeout}ms`);
   189→        }
   190→        throw error;
   191→    }
   192→}
   193→
   194→/**
   195→ * Parse error response from backend
   196→ * @param {Response} response - Fetch response
   197→ */
   198→async function parseErrorResponse(response) {
   199→    try {
   200→        const data = await response.json();
   201→        return data.error || data.message || `HTTP ${response.status}`;
   202→    } catch {
   203→        return `HTTP ${response.status}: ${response.statusText}`;
   204→    }
   205→}
   206→
   207→/**
   208→ * Get auth headers for API requests
   209→ */
   210→function getAuthHeaders() {
   211→    const headers = { 'Content-Type': 'application/json' };
   212→    const settings = getSettings();
   213→
   214→    if (settings.customerId) {
   215→        headers['x-stripe-customer-id'] = settings.customerId;
   216→    }
   217→    if (settings.licenseKey) {
   218→        headers['x-license-key'] = settings.licenseKey;
   219→    }
   220→
   221→    return headers;
   222→}
