   229→    sensitivity: 50,
   230→    minSilenceLength: 0.5,
   231→    sourceIsolated: false,
   232→    enableTakesDetection: true,
   233→    enableJCut: false,
   234→    jcutLeadIn: 0.3,
   235→    jcutLeadOut: 0.2,
   236→    enableZoom: false,
   237→    zoomFrequency: 'medium',
   238→    zoomPreset: 'medium',
   239→    zoomPlacement: 'sentence_start',
   240→    enableChapters: false,
   241→    maxChapters: 10,
   242→    minChapterLength: 60,
   243→    enableProfanity: false,
   244→    profanityLanguage: 'en',
   245→    bleepType: 'standard',
   246→    customerId: null,
   247→    licenseKey: null,
   248→    expandedOptions: false,
   249→    activePreset: 'podcast',
   250→    rememberOptions: false
