   252→
   253→/**
   254→ * Get settings from localStorage
   255→ */
   256→function getSettings() {
   257→    try {
   258→        const stored = localStorage.getItem('spliceSettings');
   259→        if (stored) {
   260→            return { ...DEFAULT_SETTINGS, ...JSON.parse(stored) };
   261→        }
   262→    } catch (e) {
   263→        console.warn('[Config] Error loading settings:', e);
   264→    }
   265→    return { ...DEFAULT_SETTINGS };
   266→}
   267→
   268→/**
   269→ * Save settings to localStorage
   270→ */
   271→function saveSettings(settings) {
   272→    try {
   273→        const merged = { ...getSettings(), ...settings };
   274→        localStorage.setItem('spliceSettings', JSON.stringify(merged));
   275→        return true;
   276→    } catch (e) {
   277→        console.warn('[Config] Error saving settings:', e);
   278→        return false;
   279→    }
   280→}
   281→
   282→/**
   283→ * Clear specific setting
   284→ */
   285→function clearSetting(key) {
   286→    const settings = getSettings();
   287→    delete settings[key];
   288→    localStorage.setItem('spliceSettings', JSON.stringify(settings));
   289→}
