   359→// ============================================================================
   360→// UTILITY FUNCTIONS
   361→// ============================================================================
   362→
   363→/**
   364→ * SECURITY: Escape HTML to prevent XSS attacks
   365→ * Use this when inserting untrusted content into innerHTML
   366→ * @param {string} str - String to escape
   367→ * @returns {string} Escaped string safe for innerHTML
   368→ */
   369→function escapeHtml(str) {
   370→    if (str === null || str === undefined) return '';
   371→    if (typeof str !== 'string') str = String(str);
   372→
   373→    const escapeMap = {
   374→        '&': '&amp;',
   375→        '<': '&lt;',
   376→        '>': '&gt;',
   377→        '"': '&quot;',
   378→        "'": '&#x27;',
   379→        '/': '&#x2F;',
   380→        '`': '&#x60;'
   381→    };
   382→
   383→    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
   384→}
   385→
   386→/**
   387→ * SECURITY: Create safe HTML from template with escaped values
   388→ * @param {TemplateStringsArray} strings - Template strings
   389→ * @param {...any} values - Values to escape
   390→ * @returns {string} HTML with escaped values
   391→ */
   392→function safeHtml(strings, ...values) {
   393→    return strings.reduce((result, str, i) => {
   394→        const value = values[i - 1];
   395→        const escaped = escapeHtml(value);
   396→        return result + escaped + str;
   397→    });
   398→}
   399→
   400→/**
   401→ * Format time in MM:SS or HH:MM:SS
   402→ */
   403→function formatTime(seconds) {
   404→    if (isNaN(seconds) || seconds < 0) return '0:00';
   405→
   406→    const hours = Math.floor(seconds / 3600);
   407→    const minutes = Math.floor((seconds % 3600) / 60);
   408→    const secs = Math.floor(seconds % 60);
   409→
   410→    if (hours > 0) {
   411→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
   412→    }
   413→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
   414→}
   415→
   416→/**
   417→ * Set status message
   418→ */
   419→function setStatus(message, isError = false) {
   420→    const status = document.getElementById('status');
   421→    if (status) {
   422→        status.textContent = message;
   423→        status.style.color = isError ? '#dc3545' : '#888';
   424→    }
   425→}
   426→
   427→/**
   428→ * Debounce function calls
   429→ */
   430→function debounce(func, wait) {
   431→    let timeout;
   432→    return function executedFunction(...args) {
   433→        const later = () => {
   434→            clearTimeout(timeout);
   435→            func(...args);
   436→        };
   437→        clearTimeout(timeout);
   438→        timeout = setTimeout(later, wait);
   439→    };
   440→}
