   550→/**
   551→ * List of built-in preset IDs
   552→ */
   553→const BUILT_IN_PRESET_IDS = ['custom', 'podcast', 'interview', 'reaction', 'tutorial', 'vlog'];
   554→
   555→/**
   556→ * Get all available presets
   557→ * @returns {Object} All preset definitions
   558→ */
   559→function getPresets() {
   560→    return { ...PRESETS };
   561→}
   562→
   563→/**
   564→ * Get a specific preset by name
   565→ * @param {string} presetName - Name of the preset
   566→ * @returns {Object|null} Preset definition or null if not found
   567→ */
   568→function getPreset(presetName) {
   569→    return PRESETS[presetName] || null;
   570→}
   571→
   572→/**
   573→ * Check if a preset is a built-in preset
   574→ * @param {string} id - Preset ID to check
   575→ * @returns {boolean} True if built-in, false if custom
   576→ */
   577→function isBuiltInPreset(id) {
   578→    return BUILT_IN_PRESET_IDS.includes(id);
   579→}
