     1→/**
     2→ * SPLICE Plugin Configuration
     3→ *
     4→ * Constants and configuration values used across all slices.
     5→ */
     6→
     7→// Backend API configuration
     8→const BACKEND_URL_DEV = 'https://127.0.0.1:3847';
     9→const BACKEND_URL_PROD = 'https://splice-api-production.up.railway.app';
    10→
    11→// Default to dev URL - production detection handled by getBackendUrl()
    12→// Default to production URL for release
    13→const BACKEND_URL = BACKEND_URL_PROD;
    14→
    15→/**
    16→ * Get the backend URL with consistent fallback logic.
    17→ * Used across all plugin modules for API calls.
    18→ * @returns {string} Backend API base URL
    19→ */
    20→function getBackendUrl() {
    21→  // Check if explicitly defined
    22→  if (typeof BACKEND_URL !== 'undefined' && BACKEND_URL) {
    23→    return BACKEND_URL;
    24→  }
    25→  // Fallback to dev URL (use 127.0.0.1, not localhost - UXP requirement)
    26→  return BACKEND_URL_DEV;
    27→}
    28→
    29→// File paths - initialized dynamically for cross-platform support
    30→// These are populated by initPaths() on plugin load
    31→let WAV_PATH = null;
    32→let XML_PATH = null;
    33→let WAV_PRESET_PATH = null;
    34→
    35→// Cache for initialized paths
    36→let _pathsInitialized = false;
    37→
    38→/**
    39→ * Initialize file paths using UXP localFileSystem API.
    40→ * Must be called once on plugin load before using file operations.
    41→ * Cross-platform: works on macOS and Windows.
    42→ * @returns {Promise<{wavPath: string, xmlPath: string, presetPath: string|null}>}
    43→ */
    44→async function initPaths() {
    45→  if (_pathsInitialized && WAV_PATH && XML_PATH) {
    46→    return { wavPath: WAV_PATH, xmlPath: XML_PATH, presetPath: WAV_PRESET_PATH };
    47→  }
    48→
    49→  try {
    50→    const { localFileSystem } = require('uxp').storage;
    51→
    52→    // Get UXP temp folder (cross-platform)
    53→    const tempFolder = await localFileSystem.getTemporaryFolder();
    54→
    55→    // Create temp files in UXP temp directory
    56→    const wavFile = await tempFolder.createFile('splice_audio_export.wav', { overwrite: true });
    57→    const xmlFile = await tempFolder.createFile('splice_export.xml', { overwrite: true });
    58→
    59→    WAV_PATH = wavFile.nativePath;
    60→    XML_PATH = xmlFile.nativePath;
    61→
    62→    // Detect Premiere Pro preset path dynamically
    63→    WAV_PRESET_PATH = await detectPresetPath();
    64→
    65→    _pathsInitialized = true;
    66→    console.log('[SPLICE Config] Paths initialized:', { WAV_PATH, XML_PATH, WAV_PRESET_PATH });
    67→
    68→    return { wavPath: WAV_PATH, xmlPath: XML_PATH, presetPath: WAV_PRESET_PATH };
    69→  } catch (err) {
    70→    console.error('[SPLICE Config] Failed to initialize paths:', err);
    71→
    72→    // Fallback to platform-specific temp paths
    73→    const isMac = navigator.platform.toLowerCase().includes('mac');
    74→    const tempDir = isMac ? '/tmp' : (process.env.TEMP || process.env.TMP || 'C:\\Windows\\Temp');
    75→
    76→    WAV_PATH = `${tempDir}/splice_audio_export.wav`;
    77→    XML_PATH = `${tempDir}/splice_export.xml`;
    78→    WAV_PRESET_PATH = await detectPresetPath();
    79→
    80→    _pathsInitialized = true;
    81→    console.log('[SPLICE Config] Using fallback paths:', { WAV_PATH, XML_PATH, WAV_PRESET_PATH });
    82→
    83→    return { wavPath: WAV_PATH, xmlPath: XML_PATH, presetPath: WAV_PRESET_PATH };
    84→  }
    85→}
    86→
    87→/**
    88→ * Detect Premiere Pro encoder preset path dynamically.
    89→ * Searches multiple possible locations for different versions.
    90→ * @returns {Promise<string|null>} Preset path or null if not found
    91→ */
    92→async function detectPresetPath() {
    93→  const isMac = navigator.platform.toLowerCase().includes('mac');
    94→
    95→  // Possible Premiere Pro installation paths
    96→  const possiblePaths = isMac ? [
    97→    // macOS paths - try multiple versions
    98→    '/Applications/Adobe Premiere Pro 2025/Adobe Premiere Pro 2025.app/Contents/Settings/EncoderPresets/Wave48mono16.epr',
    99→    '/Applications/Adobe Premiere Pro (Beta)/Adobe Premiere Pro (Beta).app/Contents/Settings/EncoderPresets/Wave48mono16.epr',
   100→    '/Applications/Adobe Premiere Pro 2024/Adobe Premiere Pro 2024.app/Contents/Settings/EncoderPresets/Wave48mono16.epr',
   101→    '/Applications/Adobe Premiere Pro/Adobe Premiere Pro.app/Contents/Settings/EncoderPresets/Wave48mono16.epr'
   102→  ] : [
   103→    // Windows paths
   104→    'C:\\Program Files\\Adobe\\Adobe Premiere Pro 2025\\Settings\\EncoderPresets\\Wave48mono16.epr',
   105→    'C:\\Program Files\\Adobe\\Adobe Premiere Pro (Beta)\\Settings\\EncoderPresets\\Wave48mono16.epr',
   106→    'C:\\Program Files\\Adobe\\Adobe Premiere Pro 2024\\Settings\\EncoderPresets\\Wave48mono16.epr',
   107→    'C:\\Program Files\\Adobe\\Adobe Premiere Pro\\Settings\\EncoderPresets\\Wave48mono16.epr'
   108→  ];
   109→
   110→  // Try to find existing preset file
   111→  try {
   112→    const { localFileSystem } = require('uxp').storage;
   113→
   114→    for (const path of possiblePaths) {
   115→      try {
   116→        // Use UXP entry API to check if file exists
   117→        const entry = await localFileSystem.getEntryWithUrl('file://' + encodeURI(path));
   118→        if (entry) {
   119→          console.log('[SPLICE Config] Found preset at:', path);
   120→          return path;
   121→        }
   122→      } catch (e) {
   123→        // File doesn't exist at this path, continue checking
   124→      }
   125→    }
   126→  } catch (err) {
   127→    console.warn('[SPLICE Config] Could not check preset paths:', err);
   128→  }
   129→
   130→  // Return first path as fallback (user can manually configure if needed)
   131→  console.warn('[SPLICE Config] Preset path not found, using default');
   132→  return possiblePaths[0];
   133→}
   134→
   135→/**
   136→ * Get initialized paths (async getter).
   137→ * Ensures paths are initialized before returning.
   138→ * @returns {Promise<{wavPath: string, xmlPath: string, presetPath: string|null}>}
   139→ */
   140→async function getPaths() {
   141→  if (!_pathsInitialized) {
   142→    return await initPaths();
   143→  }
   144→  return { wavPath: WAV_PATH, xmlPath: XML_PATH, presetPath: WAV_PRESET_PATH };
   145→}
   146→
   147→/**
   148→ * Check if a path exists and is accessible.
   149→ * @param {string} path - File path to check
   150→ * @returns {Promise<boolean>} True if path exists
   151→ */
   152→async function pathExists(path) {
   153→  if (!path) return false;
   154→
   155→  try {
   156→    const { localFileSystem } = require('uxp').storage;
   157→    const entry = await localFileSystem.getEntryWithUrl('file://' + encodeURI(path));
   158→    return entry !== null;
   159→  } catch {
   160→    return false;
   161→  }
   162→}
   163→
