// Atomic operation lock (prevents race conditions)
let operationLock = {
  locked: false,
  async acquire() {
    while (this.locked) {
      await new Promise(resolve => setTimeout(resolve, 50));
    }
    this.locked = true;
  },
  release() {
    this.locked = false;
  }
};

