// Initialize Premiere Pro API
console.log('[SPLICE] Top-level script start');
const ppro = (typeof require !== 'undefined') ? require('premierepro') : {};

// ============================================================================
// CACHED DOM ELEMENTS (Performance: avoid repeated getElementById calls)
// ============================================================================
