/**
 * Health Check Routes
 *
 * System health and status endpoints
 * HIGH-009: Enhanced with Redis connectivity, liveness and readiness probes
 * MEDIUM-016: Added BullMQ queue depth monitoring
 */

const express = require('express');
const { isFFprobeInstalled } = require('../services/ffprobeSilence');
const { isReplicateConfigured } = require('../services/vocalIsolation');
const { checkDatabase } = require('../services/usageTracking');
const { getAllCircuitBreakerMetrics, getCircuitBreakerHealth } = require('../config/circuitBreakers');
const redis = require('../services/redisClient');
const { getQueueStats, getDeadLetterQueueStats } = require('../services/musicQueue');

/**
 * Create health routes
 * @param {Object} options - Route configuration options
 * @returns {express.Router}
 */
function createHealthRoutes(_options = {}) {
  const router = express.Router();

  /**
   * GET / - API information
   */
  router.get('/', (req, res) => {
    res.json({
      service: 'splice-backend',
      version: '6.0.3',
      endpoints: {
        'GET /': 'This info',
        'GET /health': 'Health check',
        'GET /health/circuit-breakers': 'Circuit breaker metrics for all external services',
        'GET /ffprobe-check': 'Check if FFprobe is installed',
        'GET /replicate-check': 'Check if Replicate API is configured',
        'POST /analyze': 'Analyze WAV file { wavPath }',
        'POST /silences': 'Detect silences via Whisper gaps { wavPath, threshold: 0.5 }',
        'POST /silences-audio': 'Detect silences via FFprobe { wavPath, threshold: -30, minDuration: 0.5, padding: 0.1 }',
        'POST /silences-rms': 'Detect silences via RMS analysis { wavPath, threshold: -30, minSilenceLength: 0.5, paddingStart: 0.1, paddingEnd: 0.05, autoThreshold: false, sensitivity: 50 }',
        'POST /profanity': 'Detect profanity in transcript { wavPath, language: "en", customBlocklist: [], customAllowlist: [] }',
        'GET /profanity/languages': 'Get supported languages for profanity detection',
        'GET /profanity/bleeps': 'Get available bleep sounds',
        'POST /repetitions': 'Detect phrase repetitions and stutters { wavPath, phraseSize: 5, tolerance: 0.7, useOpenAI: false }',
        'POST /fillers': 'Detect filler words (um, uh, like, etc.) { wavPath, customFillers: [] }',
        'POST /stutters': 'Detect single-word stutters only { wavPath, minRepeats: 2 }',
        'POST /export/captions': 'Export transcript to caption format { wavPath, format: srt|vtt|txt|json, outputPath? }',
        'GET /export/formats': 'Get supported caption export formats',
        'POST /multitrack': 'Analyze multiple audio tracks for multicam { audioPaths: [], speakerNames: [], videoTrackMapping: {} }',
        'POST /multitrack/auto-balance': 'Auto-balance speaker screentime { audioPaths: [], speakerNames: [] }',
        'POST /multitrack/advanced-balance': 'Advanced GA-optimized balancing { audioPaths: [], maxConsecutiveSeconds: 30, momentumFactor: 0.7 }',
        'POST /process-xml': 'Process FCP XML { xmlPath, silences, removeGaps: true }',
        'POST /cut-list': 'Generate JSON cut list for DOM building (v3.5) { sourceName, sourcePath, duration, silences, takes?, settings? }',
        'POST /cut-list/takes': 'Generate cut list keeping only takes { sourceName, sourcePath, duration, takes, settings? }',
        'POST /zoom': 'Generate zoom points from transcript { transcript, settings: { frequency, preset, placement } }',
        'GET /zoom/presets': 'Get available zoom presets and frequencies',
        'POST /chapters': 'Detect chapters in transcript using AI { transcript, settings: { maxChapters, minChapterLength } }',
        'POST /chapters/fallback': 'Detect chapters without AI (gap-based) { transcript, settings }',
        'POST /isolate-vocals': 'Isolate vocals from audio { audioPath }',
        'POST /batch/silences': 'Batch process multiple files for silence detection { files: [], options: {} }',
        'GET /batch/status/:jobId': 'Get batch job status',
        'GET /batch/results/:jobId': 'Get full batch job results',
        'GET /batch/jobs': 'List all batch jobs',
        'DELETE /batch/:jobId': 'Delete a batch job',
        'GET /credits': 'Get user credit balance (requires x-stripe-customer-id header)',
        'GET /usage-history': 'Get usage history (requires x-stripe-customer-id header)',
        'POST /webhooks/stripe': 'Stripe webhook endpoint',
        'GET /referral/code': 'Get or create referral code for user',
        'POST /referral/validate': 'Validate a referral code',
        'POST /referral/apply': 'Apply referral code at signup',
        'GET /referral/stats': 'Get referral statistics for user',
        'POST /license/activate': 'Activate license key { key: "SPLICE-XXXX-XXXX-XXXX" }',
        'GET /license/key': 'Get license key for customer (requires x-stripe-customer-id)',
        'POST /license/resend': 'Resend license key to customer email { customerId? }'
      }
    });
  });

  /**
   * GET /health - Comprehensive health check with all dependencies
   * HIGH-009: Enhanced with Redis connectivity and external service status
   */
  router.get('/health', async (req, res) => {
    const health = {
      status: 'ok',
      service: 'splice-backend',
      version: '6.0.3',
      timestamp: new Date().toISOString(),
      checks: {}
    };

    // Database check
    try {
      const dbHealthy = await checkDatabase();
      health.checks.database = { status: dbHealthy ? 'ok' : 'error' };
      if (!dbHealthy) {
        health.status = 'degraded';
      }
    } catch (err) {
      health.checks.database = { status: 'error', message: err.message };
      health.status = 'degraded';
      console.error('[Health] Database check failed:', err.message);
    }

    // Redis check
    try {
      const redisHealthy = await redis.pingRedis();
      health.checks.redis = {
        status: redisHealthy ? 'ok' : 'error',
        type: redis.getRedisStatus().type
      };
      if (!redisHealthy) {
        health.status = 'degraded';
      }
    } catch (err) {
      health.checks.redis = { status: 'error', message: err.message };
      health.status = 'degraded';
      console.error('[Health] Redis check failed:', err.message);
    }

    // External API configuration status (lightweight checks)
    health.checks.stripe = {
      status: process.env.STRIPE_SECRET_KEY ? 'configured' : 'missing'
    };
    health.checks.sendgrid = {
      status: process.env.SENDGRID_API_KEY ? 'configured' : 'missing'
    };
    health.checks.openai = {
      status: process.env.OPENAI_API_KEY ? 'configured' : 'missing'
    };
    health.checks.replicate = {
      status: process.env.REPLICATE_API_TOKEN ? 'configured' : 'missing'
    };

    const statusCode = health.status === 'ok' ? 200 : 503;
    res.status(statusCode).json(health);
  });

  /**
   * GET /health/live - Kubernetes liveness probe
   * HIGH-009: Simple check that the process is alive and can respond
   * Returns 200 if the server is running, used to detect deadlocks
   */
  router.get('/health/live', (req, res) => {
    res.status(200).json({
      status: 'alive',
      timestamp: new Date().toISOString()
    });
  });

  /**
   * GET /health/ready - Kubernetes readiness probe
   * HIGH-009: Checks that all critical dependencies are available
   * Returns 503 if not ready to accept traffic (e.g., during startup or when dependencies are down)
   */
  router.get('/health/ready', async (req, res) => {
    const checks = {
      database: false,
      redis: false
    };

    // Check database connectivity
    try {
      checks.database = await checkDatabase();
    } catch (err) {
      console.error('[Health/Ready] Database check failed:', err.message);
    }

    // Check Redis connectivity
    try {
      checks.redis = await redis.pingRedis();
    } catch (err) {
      console.error('[Health/Ready] Redis check failed:', err.message);
    }

    // Ready only if all critical dependencies are available
    const isReady = checks.database && checks.redis;

    res.status(isReady ? 200 : 503).json({
      status: isReady ? 'ready' : 'not ready',
      timestamp: new Date().toISOString(),
      checks
    });
  });

  /**
   * GET /ffprobe-check - Check if FFprobe is installed
   */
  router.get('/ffprobe-check', async (req, res) => {
    const installed = await isFFprobeInstalled();
    res.json({
      installed,
      message: installed
        ? 'FFprobe is available'
        : 'FFprobe not found. Install with: brew install ffmpeg'
    });
  });

  /**
   * GET /replicate-check - Check if Replicate API is configured
   */
  router.get('/replicate-check', async (req, res) => {
    const configured = isReplicateConfigured();
    res.json({
      configured,
      message: configured
        ? 'Replicate API is configured'
        : 'REPLICATE_API_TOKEN not set. Add to .env file.'
    });
  });

  /**
   * GET /health/circuit-breakers - Get circuit breaker metrics for all external services
   *
   * Returns state, failure/success counts, and last state change for each circuit breaker.
   * Useful for monitoring dashboards and alerts.
   */
  router.get('/health/circuit-breakers', (req, res) => {
    try {
      const metrics = getAllCircuitBreakerMetrics();
      const health = getCircuitBreakerHealth();

      res.json({
        timestamp: new Date().toISOString(),
        overallHealth: health,
        circuitBreakers: metrics
      });
    } catch (err) {
      console.error('[Health] Failed to get circuit breaker metrics:', err.message);
      res.status(500).json({
        error: 'Failed to retrieve circuit breaker metrics',
        message: err.message
      });
    }
  });

  /**
   * GET /health/queues - BullMQ queue depth monitoring
   * MEDIUM-016: Queue depth monitoring for operational visibility
   *
   * Returns queue statistics for music generation queue and dead letter queue.
   * Used for monitoring queue health, detecting backlogs, and alerting.
   *
   * Alert thresholds (configurable in monitoring system):
   * - waiting > 100: Queue backlog building
   * - failed > 10: High failure rate
   * - dlq.pending > 50: Dead letter queue needs attention
   */
  router.get('/health/queues', async (req, res) => {
    try {
      // Get main queue stats
      const queueStats = await getQueueStats();

      // Get dead letter queue stats
      let dlqStats = null;
      try {
        dlqStats = await getDeadLetterQueueStats();
      } catch (dlqErr) {
        console.warn('[Health/Queues] DLQ stats unavailable:', dlqErr.message);
      }

      // Determine overall health
      const health = {
        status: 'ok',
        warnings: []
      };

      // Check for queue backlog
      if (queueStats.waiting > 100) {
        health.status = 'warning';
        health.warnings.push(`Queue backlog: ${queueStats.waiting} jobs waiting`);
      }

      // Check for high failure rate
      if (queueStats.failed > 10) {
        health.status = 'warning';
        health.warnings.push(`High failure rate: ${queueStats.failed} failed jobs`);
      }

      // Check DLQ for unprocessed failures
      if (dlqStats && dlqStats.pending > 50) {
        health.status = 'warning';
        health.warnings.push(`DLQ needs attention: ${dlqStats.pending} unrecovered jobs`);
      }

      res.json({
        timestamp: new Date().toISOString(),
        health,
        queues: {
          musicGeneration: {
            name: 'music-generation',
            ...queueStats,
            // Calculate utilization metrics
            utilization: queueStats.active > 0 ? 'active' : 'idle',
            backlogRatio: queueStats.total > 0
              ? ((queueStats.waiting + queueStats.delayed) / queueStats.total * 100).toFixed(1) + '%'
              : '0%'
          },
          deadLetterQueue: dlqStats ? {
            name: 'music-generation-dlq',
            ...dlqStats
          } : { status: 'unavailable' }
        }
      });
    } catch (err) {
      console.error('[Health/Queues] Failed to get queue stats:', err.message);
      res.status(500).json({
        error: 'Failed to retrieve queue statistics',
        message: err.message,
        timestamp: new Date().toISOString()
      });
    }
  });

  return router;
}

module.exports = createHealthRoutes;
