   461→function debounce(func, wait) {
   462→    let timeout;
   463→    return function executedFunction(...args) {
   464→        const later = () => {
   465→            clearTimeout(timeout);
   466→            func(...args);
   467→        };
   468→        clearTimeout(timeout);
   469→        timeout = setTimeout(later, wait);
