/**
 * Fetch with timeout support
 * @param {string} url - URL to fetch
 * @param {object} options - Fetch options
 * @param {number} timeout - Timeout in ms
 */
async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
    // DIAGNOSTIC: Log fetch request details before making request
    console.log('[SPLICE FETCH] === FETCH REQUEST START ===');
    console.log('[SPLICE FETCH] URL:', url);
    console.log('[SPLICE FETCH] Method:', options?.method || 'GET');
    console.log('[SPLICE FETCH] Headers:', options?.headers);
    console.log('[SPLICE FETCH] Timeout:', timeout);
    console.log('[SPLICE FETCH] Is CEP environment:', typeof CSInterface !== 'undefined');
    console.log('[SPLICE FETCH] =================================');

    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    try {
        const response = await fetch(url, {
            ...options,
            signal: controller.signal
        });
        clearTimeout(timeoutId);

        // DIAGNOSTIC: Log successful response
        console.log('[SPLICE FETCH] === FETCH RESPONSE SUCCESS ===');
        console.log('[SPLICE FETCH] Status:', response.status);
        console.log('[SPLICE FETCH] OK:', response.ok);
        console.log('[SPLICE FETCH] =================================');

        return response;
    } catch (error) {
        clearTimeout(timeoutId);

        // DIAGNOSTIC: Log raw fetch error before rethrowing
        console.log('[SPLICE FETCH] === FETCH ERROR CAUGHT ===');
        console.log('[SPLICE FETCH] Error name:', error?.name);
        console.log('[SPLICE FETCH] Error message:', error?.message);
        console.log('[SPLICE FETCH] Error type:', typeof error);
        console.log('[SPLICE FETCH] Error constructor:', error?.constructor?.name);
        console.log('[SPLICE FETCH] Is AbortError:', error.name === 'AbortError');
        console.log('[SPLICE FETCH] Is TypeError:', error.name === 'TypeError');
        console.log('[SPLICE FETCH] Full error object:', JSON.stringify(error, Object.getOwnPropertyNames(error)));
        console.log('[SPLICE FETCH] =================================');

        if (error.name === 'AbortError') {
            throw new Error(`Request timeout after ${timeout}ms`);
        }
        throw error;
    }
}

/**
 * Parse error response from backend
 * @param {Response} response - Fetch response
 */
async function parseErrorResponse(response) {
    try {
        const data = await response.json();
        return data.error || data.message || `HTTP ${response.status}`;
    } catch {
        return `HTTP ${response.status}: ${response.statusText}`;
    }
}

/**
 * Get auth headers for API requests
 */
function getAuthHeaders() {
    const headers = { 'Content-Type': 'application/json' };
    const settings = getSettings();

    if (settings.customerId) {
        headers['x-stripe-customer-id'] = settings.customerId;
    }
    if (settings.licenseKey) {
        headers['x-license-key'] = settings.licenseKey;
    }

    return headers;
}

