   108→            resolve(this._initialized);
   109→        });
   110→
   111→        return this._initPromise;
   112→    },
   113→
   114→    /**
   115→     * Ensure JSX is initialized before making calls
   116→     */
   117→    ensureInitialized: async function() {
   118→        if (!this._initialized) {
   119→            await this.init();
   120→        }
   121→        if (!this.cs) {
   122→            throw new Error('CSInterface not initialized. Make sure you are running in CEP environment.');
   123→        }
   124→    },
   125→
   126→    /**
   127→     * Execute ExtendScript in Premiere Pro
   128→     * @param {string} script - ExtendScript to execute
   129→     * @returns {Promise<any>} - Parsed result
   130→     */
   131→    evalScript: async function(script) {
   132→        await this.ensureInitialized();
   133→
   134→        return new Promise((resolve, reject) => {
   135→            this.cs.evalScript(script, (result) => {
   136→                if (result === 'undefined' || result === undefined) {
   137→                    resolve(null);
   138→                    return;
   139→                }
   140→
   141→                // Check for EvalScript error or ExtendScript runtime errors
   142→                if (typeof result === 'string' && (
   143→                    result.indexOf('EvalScript error') !== -1 ||
   144→                    result.indexOf('Error:') !== -1 ||
   145→                    result.indexOf('TypeError:') !== -1 ||
   146→                    result.indexOf('ReferenceError:') !== -1
   147→                )) {
   148→                    reject(new Error(result));
   149→                    return;
   150→                }
   151→
   152→                // Try to parse JSON
   153→                try {
   154→                    const parsed = JSON.parse(result);
   155→                    if (parsed.error) {
   156→                        reject(new Error(parsed.error));
   157→                    } else {
   158→                        resolve(parsed);
   159→                    }
   160→                } catch (e) {
