   181→                return `'${arg
   182→                    .replace(/\\/g, '\\\\')
   183→                    .replace(/'/g, "\\'")
   184→                    .replace(/\r/g, '\\r')
   185→                    .replace(/\n/g, '\\n')
   186→                    .replace(/\t/g, '\\t')}'`;
   187→            }
   188→            if (typeof arg === 'object') {
