   428→/**
   429→ * SECURITY: Escape HTML to prevent XSS attacks
   430→ * Use this when inserting untrusted content into innerHTML
   431→ * @param {string} str - String to escape
   432→ * @returns {string} Escaped string safe for innerHTML
   433→ */
   434→function escapeHtml(str) {
   435→    if (str === null || str === undefined) return '';
   436→    if (typeof str !== 'string') str = String(str);
   437→
   438→    const escapeMap = {
   439→        '&': '&amp;',
   440→        '<': '&lt;',
   441→        '>': '&gt;',
   442→        '"': '&quot;',
   443→        "'": '&#x27;',
   444→        '/': '&#x2F;',
   445→        '`': '&#x60;'
   446→    };
   447→
   448→    return str.replace(/[&<>"'\/`]/g, char => escapeMap[char]);
   449→}
   450→
   451→/**
   452→ * SECURITY: Create safe HTML from template with escaped values
   453→ * @param {TemplateStringsArray} strings - Template strings
   454→ * @param {...any} values - Values to escape
   455→ * @returns {string} HTML with escaped values
   456→ */
   457→function safeHtml(strings, ...values) {
   458→    return strings.reduce((result, str, i) => {
   459→        const value = values[i - 1];
   460→        const escaped = escapeHtml(value);
   461→        return result + escaped + str;
   462→    });
   463→}
