   465→/**
   466→ * Format time in MM:SS or HH:MM:SS
   467→ */
   468→function formatTime(seconds) {
   469→    if (isNaN(seconds) || seconds < 0) return '0:00';
   470→
   471→    const hours = Math.floor(seconds / 3600);
   472→    const minutes = Math.floor((seconds % 3600) / 60);
   473→    const secs = Math.floor(seconds % 60);
   474→
   475→    if (hours > 0) {
   476→        return `${hours}:${minutes.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
   477→    }
   478→    return `${minutes}:${secs.toString().padStart(2, '0')}`;
   479→}
