   481→/**
   482→ * Set status message
   483→ */
   484→function setStatus(message, isError = false) {
   485→    const status = document.getElementById('status');
   486→    if (status) {
   487→        status.textContent = message;
   488→        status.style.color = isError ? '#dc3545' : '#888';
