   492→/**
   493→ * Debounce function calls
   494→ */
   495→function debounce(func, wait) {
   496→    let timeout;
   497→    return function executedFunction(...args) {
   498→        const later = () => {
   499→            clearTimeout(timeout);
   500→            func(...args);
   501→        };
   502→        clearTimeout(timeout);
   503→        timeout = setTimeout(later, wait);
   504→    };
   505→}
