   752→/**
   753→ * Validate license key format: SPLICE-XXXX-XXXX-XXXX
   754→ * Character set excludes confusing chars: 0/O, 1/I/L
   755→ */
   756→function isValidLicenseKeyFormat(key) {
   757→    if (!key || typeof key !== 'string') return false;
   758→    const normalized = key.toUpperCase().trim();
   759→    const pattern = /^SPLICE-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}-[ABCDEFGHJKMNPQRSTUVWXYZ23456789]{4}$/;
   760→    return pattern.test(normalized);
   761→}
