   827→
   828→                const result = await response.json();
   829→
   830→                if (!result.success) {
   831→                    showLoginError(result.error || 'Activation failed');
   832→                    return;
   833→                }
   834→
   835→                saveSettings({ customerId: result.customerId });
   836→                loginModal?.classList.add('hidden');
   837→
   838→                if (typeof refreshCredits === 'function') {
   839→                    await refreshCredits();
   840→                }
   841→
   842→                setStatus(`License activated! ${result.tierName} tier`);
   843→            } catch (err) {
   844→                console.error('[SPLICE] License activation error:', err);
   845→
   846→                // DIAGNOSTIC LOGGING: Capture exact error details (debug mode only)
   847→                debugLog('=== CEP LICENSE ACTIVATION ERROR DIAGNOSTICS ===');
   848→                debugLog('Error name:', err?.name);
   849→                debugLog('Error message:', err?.message);
   850→                debugLog('Error stack:', err?.stack);
   851→                debugLog('Backend URL:', getBackendUrl());
   852→                debugLog('Fetch timeout:', SPLICE_CONFIG.FETCH_TIMEOUT);
   853→                debugLog('Is CEP environment:', typeof CSInterface !== 'undefined');
   854→                debugLog('User agent:', navigator?.userAgent);
   855→                debugLog('Error constructor:', err?.constructor?.name);
   856→                debugLog('Is TypeError:', err?.name === 'TypeError');
   857→                debugLog('Message contains "Failed to fetch":', err?.message?.includes('Failed to fetch'));
   858→                debugLog('Message contains "fetch":', err?.message?.toLowerCase()?.includes('fetch'));
   859→                debugLog('Message contains "network":', err?.message?.toLowerCase()?.includes('network'));
   860→                debugLog('Message contains "CORS":', err?.message?.toLowerCase()?.includes('cors'));
   861→                debugLog('Message contains "SSL":', err?.message?.toLowerCase()?.includes('ssl'));
   862→                debugLog('Message contains "certificate":', err?.message?.toLowerCase()?.includes('certificate'));
   863→                debugLog('Full error object:', JSON.stringify(err, Object.getOwnPropertyNames(err)));
   864→                debugLog('================================================');
   865→
   866→                // Provide detailed error message based on error type
   867→                let errorMessage = 'Connection error. ';
   868→
   869→                if (err.message === 'Request timed out') {
   870→                    errorMessage += 'Server is not responding. Check if backend is running.';
   871→                } else if (err.message?.includes('Failed to fetch') || err.name === 'TypeError') {
   872→                    errorMessage += `Cannot reach ${getBackendUrl()}. Check network/firewall.`;
   873→                } else if (err.message?.includes('CORS')) {
   874→                    errorMessage += 'CORS error. Contact support.';
   875→                } else if (err.message?.includes('SSL') || err.message?.includes('certificate')) {
   876→                    errorMessage += 'SSL certificate error. Try updating your system certificates.';
   877→                } else if (err.message) {
   878→                    errorMessage += err.message;
   879→                } else {
   880→                    errorMessage += 'Unknown error. Check console for details.';
   881→                }
   882→
   883→                debugLog('Final error message shown to user:', errorMessage);
