// ============================================================================
// SETTINGS MANAGEMENT
// ============================================================================

const DEFAULT_SETTINGS = {
    sensitivity: 50,
    minSilenceLength: 0.5,
    sourceIsolated: false,
    enableTakesDetection: true,
    enableJCut: false,
    jcutLeadIn: 0.3,
    jcutLeadOut: 0.2,
    enableZoom: false,
    zoomFrequency: 'medium',
    zoomPreset: 'medium',
