    sensitivity: 50,
    minSilenceLength: 0.5,
    sourceIsolated: false,
    enableTakesDetection: true,
    enableJCut: false,
    jcutLeadIn: 0.3,
    jcutLeadOut: 0.2,
    enableZoom: false,
    zoomFrequency: 'medium',
    zoomPreset: 'medium',
    zoomPlacement: 'sentence_start',
    enableChapters: false,
    maxChapters: 10,
    minChapterLength: 60,
    enableProfanity: false,
    profanityLanguage: 'en',
    bleepType: 'standard',
    customerId: null,
    licenseKey: null,
    expandedOptions: false,
    activePreset: 'podcast',
    rememberOptions: false
};

// Current storage version for future migrations
const SETTINGS_STORAGE_VERSION = 1;

// Fields that contain sensitive data and should be encrypted
const SENSITIVE_FIELDS = ['customerId', 'licenseKey'];

// ============================================================================
// ENCRYPTION HELPERS FOR SENSITIVE DATA
// ============================================================================

/**
 * Simple obfuscation for localStorage
 * NOTE: This is NOT cryptographically secure, but provides basic protection
 * against casual inspection of localStorage. For truly sensitive data,
 * consider server-side storage or proper encryption libraries.
 *
 * @param {any} data - Data to encode
 * @returns {string|null} Encoded string or null on error
 */
function encryptForStorage(data) {
    try {
        if (data === null || data === undefined) {
            return null;
        }
        // Double encode to add slight obfuscation layer
        const jsonStr = JSON.stringify(data);
        const encoded = encodeURIComponent(jsonStr);
        return btoa(encoded);
    } catch (e) {
        debugLog('[Config] Encryption error:', e);
        return null;
    }
}

/**
 * Decode obfuscated data from localStorage
 *
 * @param {string} data - Encoded string
 * @returns {any|null} Decoded data or null on error
 */
function decryptFromStorage(data) {
    try {
        if (!data || typeof data !== 'string') {
            return null;
        }
        const decoded = atob(data);
        const jsonStr = decodeURIComponent(decoded);
        return JSON.parse(jsonStr);
    } catch (e) {
        // This is expected for unencrypted legacy data - don't log as warning
        return null;
    }
}

/**
 * Check if a value appears to be encrypted (base64 encoded JSON)
 *
 * @param {any} value - Value to check
 * @returns {boolean} True if value appears to be encrypted
 */
function isEncryptedValue(value) {
    if (!value || typeof value !== 'string') {
        return false;
    }
    // Unencrypted values typically start with known patterns
    // (e.g., "cus_" for Stripe customer IDs, "SPLICE-" for license keys)
    if (value.startsWith('cus_') || value.startsWith('SPLICE-')) {
        return false;
    }
    // Check if it's a valid base64 string that decodes to valid JSON
    try {
        const decoded = atob(value);
        const jsonStr = decodeURIComponent(decoded);
        JSON.parse(jsonStr);
        return true;
    } catch {
        return false;
    }
}

/**
 * Encrypt sensitive fields in settings object
 *
 * @param {Object} settings - Settings object with plaintext sensitive fields
 * @returns {Object} Settings with encrypted sensitive fields
 */
function encryptSensitiveFields(settings) {
    const encrypted = { ...settings };

    for (const field of SENSITIVE_FIELDS) {
        if (encrypted[field] !== null && encrypted[field] !== undefined) {
