  initLoginModal();
  initOptionsToggles();
  initPresetSelector();
  initHelpButton();

  // Credits display
  initCredits();

  // Unified workflow (silences + takes)
  initUnifiedWorkflow();

  // Preview handlers (event delegation)
  initPreviewHandlers();

  // Undo handlers
  initUndoHandlers();

  // Keyboard shortcuts
  initKeyboardShortcuts();

