  margin-bottom: 4px;
}
.audio-source-group sp-checkbox {
  display: block;
  margin: 4px 0;
  font-size: 11px;
}
.tier-badge {
  font-size: 8px;
  background: #4a9eff;
  color: white;
  padding: 1px 4px;
  border-radius: 2px;
  margin-left: 4px;
  vertical-align: middle;
}

/* Results Area */
.results-area {
  background: #1e1e1e;
  border-radius: 6px;
  padding: 12px;
  min-height: 60px;
}
.results-area.hidden {
  display: none;
}
.results-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 8px;
}
.results-header h3 {
  font-size: 11px;
  font-weight: 600;
  margin: 0;
  color: #aaa;
  text-transform: uppercase;
}
.undo-btn {
  background: #dc3545;
  color: white;
  border: none;
  padding: 4px 10px;
  border-radius: 3px;
  font-size: 10px;
  cursor: pointer;
}
.undo-btn:hover {
  background: #c82333;
}

/* Progress */
.progress-container {
  text-align: center;
  padding: 16px 0;
}
.progress-container sp-progressbar {
  width: 100%;
  margin-bottom: 8px;
}
.progress-text {
  font-size: 11px;
  color: #888;
}

/* Results Content */
.results-content {
  font-size: 11px;
}
.results-stat {
  display: flex;
  justify-content: space-between;
  padding: 4px 0;
  border-bottom: 1px solid #333;
}
.results-stat:last-child {
  border-bottom: none;
}
.results-stat-label {
  color: #888;
}
.results-stat-value {
  font-weight: 600;
  color: #4a9eff;
}

/* Preview Section */
.preview-section {
  display: none;
}
.preview-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 10px;
}
.preview-header h3 {
  font-size: 11px;
  font-weight: 600;
  margin: 0;
  color: #aaa;
  text-transform: uppercase;
}
.preview-header-actions {
  display: flex;
  gap: 4px;
}
.preview-summary {
  font-size: 10px;
  color: #888;
  margin-bottom: 10px;
  padding: 8px;
  background: #252525;
  border-radius: 4px;
}
.preview-summary strong {
  color: #4a9eff;
}
.preview-list {
  max-height: 180px;
  overflow-y: auto;
  margin-bottom: 10px;
  contain: layout style paint;
  will-change: scroll-position;
}
.preview-item {
  display: flex;
  align-items: center;
  padding: 8px 10px;
  min-height: 44px;
  margin-bottom: 4px;
  background: #2a2a2a;
  border-radius: 4px;
  border-left: 3px solid #4a9eff;
  cursor: pointer;
  transition: background-color 0.15s ease, border-left-color 0.15s ease;
  contain: layout style;
  box-sizing: border-box;
}
.preview-item:hover {
  background: #333;
}
.preview-item:focus-within {
  outline: 2px solid #4a9eff;
  outline-offset: -2px;
}
.preview-item.excluded {
  opacity: 0.65;
  border-left-color: #666;
}
.preview-item-check {
  min-width: 20px;
  min-height: 20px;
  margin-right: 10px;
  accent-color: #4a9eff;
  cursor: pointer;
}
.preview-item-info {
  flex: 1;
  min-width: 0;
  overflow: hidden;
}
.preview-item-time {
  font-size: 11px;
  font-weight: 500;
  color: #e1e1e1;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.preview-item-duration {
  font-size: 9px;
  color: #999;
  margin-top: 2px;
}
.preview-item-seek {
  background: transparent;
  border: 1px solid #555;
  color: #888;
  min-width: 32px;
  min-height: 32px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}
.preview-item-seek:hover {
  background: #444;
  color: #fff;
}
.preview-item-seek:focus-visible {
  outline: 2px solid #4a9eff;
  outline-offset: 2px;
}
.preview-item-seek.loading {

