file.content:
  pointer-events: none;
  opacity: 0.6;
}
.preview-actions {
  display: flex;
  gap: 8px;
  margin-top: 10px;
}
.preview-actions button {
  flex: 1;
  padding: 12px 10px;
  min-height: 44px;
  font-size: 11px;
  font-weight: 600;
  border-radius: 4px;
  cursor: pointer;
}
.apply-btn {
  background: linear-gradient(135deg, #28a745, #1e7e34);
  color: white;
  border: none;
}
.apply-btn:hover {
  background: linear-gradient(135deg, #34ce57, #28a745);
}
.apply-btn:disabled {
  opacity: 0.5;
  cursor: not-allowed;
}
.apply-btn:focus-visible {
  outline: 2px solid #28a745;
  outline-offset: 2px;
}
.cancel-btn {
  background: transparent;
  color: #888;
  border: 1px solid #555;
}
.cancel-btn:hover {
  background: #333;
  color: #e1e1e1;
}
.cancel-btn:focus-visible {
  outline: 2px solid #888;
  outline-offset: 2px;
}
.select-all-row {
  display: flex;
  justify-content: space-between;
  align-items: center;
  padding: 6px 0;
  margin-bottom: 8px;
  border-bottom: 1px solid #333;
}
.select-all-row label {
  font-size: 10px;
  color: #888;
  display: flex;
  align-items: center;
  cursor: pointer;
}
.select-all-row input {
  margin-right: 6px;
  min-width: 16px;
  min-height: 16px;
  accent-color: #4a9eff;
}
.preview-filters {
  display: flex;
  gap: 4px;
  flex-wrap: wrap;
  margin-bottom: 8px;
}
.filter-btn {
  background: #1e1e1e;
  border: 1px solid #444;
  color: #888;
  padding: 4px 8px;
  font-size: 9px;
  border-radius: 3px;
  cursor: pointer;
}
.filter-btn:hover {
  background: #333;
  color: #e1e1e1;
}
.filter-btn.active {
  background: #4a9eff22;
  border-color: #4a9eff;
  color: #4a9eff;
}

/* Legend dots */
.legend-item {
  display: inline-flex;
  align-items: center;
  margin-right: 12px;
}
.legend-dot {
  display: inline-block;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  margin-right: 4px;
}
.legend-dot.silence {
  background: #dc3545;
}
.legend-dot.take {
  background: #4a9eff;
}

/* Colored markers for preview items */
.preview-item.silence-marker {
  border-left-color: #dc3545;
}
.preview-item.take-marker {
  border-left-color: #4a9eff;
}
.preview-item.protected-silence {
  border-left-color: #ffc107;
  opacity: 0.6;
}
.preview-item.protected-silence::after {
  content: 'Protected';
  font-size: 8px;
  color: #ffc107;
  margin-left: 8px;
}

/* Take Items */
.takes-list {
  max-height: 200px;
  overflow-y: auto;
}
.take-item {
  padding: 8px;
  margin-bottom: 4px;
  background: #2a2a2a;
  border-radius: 4px;
  border-left: 3px solid #555;
  cursor: pointer;
  transition: all 0.15s ease;
}
.take-item:hover {
  background: #333;
}
.take-item.best {
  border-left-color: #28a745;
  background: #1a2e1a;
}
.take-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
}
.take-label {
  font-weight: 500;
  font-size: 11px;
}
.take-star {
  font-size: 14px;
  color: #555;
}
.take-item.best .take-star {
  color: #28a745;
}
.take-time {
  color: #888;
  font-size: 9px;
  margin-top: 2px;
}
.take-text {
  color: #aaa;
  font-size: 10px;
  margin-top: 4px;
}
.take-actions {
  display: flex;
  gap: 6px;
  margin-top: 10px;
}
.take-actions button {
  flex: 1;
  padding: 8px;
  font-size: 10px;
  border-radius: 4px;
}
.keep-best-btn {
  background: #28a745;
  color: white;
  border: none;
  cursor: pointer;
}
.keep-best-btn:hover {
  background: #218838;
}
.label-all-btn {
  background: #6c757d;
  color: white;

