.label-all-btn:hover {
  background: #5a6268;
}

/* Empty State */
.empty-state {
  text-align: center;
  color: #666;
  font-size: 11px;
  padding: 20px 0;
}

/* Razor Section (Advanced - hidden by default) */
.advanced-section {
  margin-top: 10px;
  padding: 10px;
  background: #252525;
  border-radius: 4px;
  border: 1px dashed #444;
}
.advanced-section.hidden {
  display: none;
}
.advanced-section h4 {
  font-size: 10px;
  color: #888;
  margin: 0 0 8px 0;
  text-transform: uppercase;
}
.advanced-section p {
  font-size: 9px;
  color: #666;
  margin: 0 0 8px 0;
}
.advanced-section button {
  font-size: 10px;
  padding: 6px 10px;
  margin-right: 6px;
}
button.razor-btn {
  background: #6f42c1;
  color: white;
  border: none;
  border-radius: 3px;
  cursor: pointer;
}
button.razor-btn:hover {
  background: #5a32a3;
}
button.export-xml-btn {
  background: #17a2b8;
  color: white;
  border: none;
  border-radius: 3px;
  cursor: pointer;
}
button.export-xml-btn:hover {
  background: #138496;
}

/* Settings Modal */
.modal {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0,0,0,0.7);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 100;
}
.modal.hidden {
  display: none;
}
.modal-content {
  background: #2a2a2a;
  border-radius: 8px;
  padding: 16px;
  width: 90%;
  max-width: 280px;
  border: 1px solid #444;
}
.modal-header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 12px;
}
.modal-header h3 {
  font-size: 12px;
  margin: 0;
}
.modal-close {
  background: transparent;
  border: none;
  color: #888;
  font-size: 18px;
  cursor: pointer;
  padding: 0;
  width: 24px;
  height: 24px;
}
.modal-close:hover {
  color: #fff;
}
.setting-group {
  margin-bottom: 12px;
}
.setting-group label {
  display: block;
  font-size: 10px;
  color: #888;
  margin-bottom: 4px;
}

/* Preset Modal Styles */
.preset-icon-option {
  color: #888;
  transition: all 0.15s ease;
}
.preset-icon-option:hover {
  background: #333 !important;
  color: #e1e1e1;
}
.preset-icon-option.selected {
  border-color: #4a9eff !important;
  background: #4a9eff22 !important;
  color: #4a9eff;
}

/* Preset List Item Styles */
.preset-list-item {
  display: flex;
  align-items: center;
  padding: 8px 10px;
  margin-bottom: 4px;
  background: #2a2a2a;
  border-radius: 4px;
  border-left: 3px solid #444;
}
.preset-list-item:hover {
  background: #333;
}
.preset-list-item.built-in {
  border-left-color: #4a9eff;
  opacity: 0.7;
}
.preset-list-item.custom {
  border-left-color: #28a745;
}
.preset-list-item-icon {
  width: 24px;
  height: 24px;
  display: flex;
  align-items: center;
  justify-content: center;
  background: #1e1e1e;
  border-radius: 4px;
  font-size: 12px;
  margin-right: 10px;
  color: #888;
}
.preset-list-item-info {
  flex: 1;
  min-width: 0;
  overflow: hidden;
}
.preset-list-item-name {
  font-size: 11px;
  font-weight: 500;
  color: #e1e1e1;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.preset-list-item-desc {
  font-size: 9px;
  color: #888;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}
.preset-list-item-actions {
  display: flex;
  gap: 4px;
}
.preset-list-item-actions button {
  background: transparent;
  border: 1px solid #555;
  color: #888;
  width: 24px;
  height: 24px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 10px;

