"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { motion } from "framer-motion";
import {
  Mail,
  MessageCircle,
  Building2,
  Users,
  Clock,
  CheckCircle,
} from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";
import { useState } from "react";

const benefits = [
  {
    icon: Users,
    title: "Custom Team Pricing",
    description: "Volume discounts for teams of 5+",
  },
  {
    icon: Building2,
    title: "Enterprise Features",
    description: "SSO, dedicated support, custom integrations",
  },
  {
    icon: Clock,
    title: "Priority Support",
    description: "Dedicated account manager and fast response times",
  },
];

export default function ContactPage() {
  const [submitted, setSubmitted] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    company: "",
    teamSize: "",
    message: "",
  });

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsLoading(true);
    setError(null);

    try {
      const response = await fetch("/api/contact", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(formData),
      });

      if (!response.ok) {
        const data = await response.json().catch(() => ({}));
        throw new Error(data.error || "Failed to send message. Please try again.");
      }

      setSubmitted(true);
    } catch (err) {
      setError(err instanceof Error ? err.message : "An unexpected error occurred. Please try again.");
    } finally {
      setIsLoading(false);
    }
  };

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement | HTMLSelectElement>) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <div className="min-h-screen bg-[#060b14] dark">
      <Header />
      <main className="pt-24 pb-16">
        <div className="container mx-auto px-4">
          {/* Hero section */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="text-center mb-12"
          >
            <Badge className="mb-4 bg-[#00D4FF]/10 text-[#00D4FF] border-[#00D4FF]/30">
              <Building2 className="w-3 h-3 mr-1" />
              Enterprise & Teams
            </Badge>
            <h1 className="text-4xl md:text-5xl font-bold mb-4 text-white">
              Contact <span className="gradient-text">Sales</span>
            </h1>
            <p className="text-lg text-gray-400 max-w-2xl mx-auto">
              Looking for team pricing or enterprise features? Let&apos;s talk about
              how SPLICE can accelerate your team&apos;s video production workflow.
            </p>
          </motion.div>

          <div className="grid lg:grid-cols-2 gap-12 max-w-6xl mx-auto">
            {/* Contact Form */}
            <motion.div
              initial={{ opacity: 0, x: -20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5, delay: 0.1 }}
            >
              <Card className="bg-[#0d1420] border-gray-800">
                <CardHeader>
                  <CardTitle className="text-white">Get in Touch</CardTitle>
                </CardHeader>
                <CardContent>
                  {submitted ? (
                    <div className="text-center py-8">
                      <div className="w-16 h-16 rounded-full bg-[#00D4FF]/20 flex items-center justify-center mx-auto mb-4">
                        <CheckCircle className="w-8 h-8 text-[#00D4FF]" />
                      </div>
                      <h3 className="text-xl font-semibold text-white mb-2">
                        Thanks for reaching out!
                      </h3>
                      <p className="text-gray-400 mb-6">
                        We&apos;ll get back to you within 24 hours.
                      </p>
                      <Button
                        variant="outline"
                        className="border-gray-700 text-white hover:bg-white/10"
                        onClick={() => setSubmitted(false)}
                      >
                        Send Another Message
                      </Button>
                    </div>
                  ) : (
                    <form onSubmit={handleSubmit} className="space-y-4">
                      <div className="grid md:grid-cols-2 gap-4">
                        <div>
                          <label className="text-sm text-gray-400 mb-1 block">
                            Your Name *
                          </label>
                          <Input
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            placeholder="John Doe"
                            required
                            className="bg-[#0a0f1a] border-gray-700 text-white"
                          />
                        </div>
                        <div>
                          <label className="text-sm text-gray-400 mb-1 block">
                            Work Email *
                          </label>
                          <Input
                            name="email"
                            type="email"
                            value={formData.email}
                            onChange={handleChange}
                            placeholder="john@company.com"
                            required
                            className="bg-[#0a0f1a] border-gray-700 text-white"
                          />
                        </div>
                      </div>
                      <div className="grid md:grid-cols-2 gap-4">
                        <div>
                          <label className="text-sm text-gray-400 mb-1 block">
                            Company Name
                          </label>
                          <Input
                            name="company"
                            value={formData.company}
                            onChange={handleChange}
                            placeholder="Acme Productions"
                            className="bg-[#0a0f1a] border-gray-700 text-white"
                          />
                        </div>
                        <div>
                          <label className="text-sm text-gray-400 mb-1 block">
                            Team Size
                          </label>
                          <select
                            name="teamSize"
                            value={formData.teamSize}
                            onChange={handleChange}
                            className="w-full h-10 px-3 rounded-md bg-[#0a0f1a] border border-gray-700 text-white"
                          >
                            <option value="">Select team size</option>
                            <option value="1-5">1-5 editors</option>
                            <option value="6-10">6-10 editors</option>
                            <option value="11-25">11-25 editors</option>
                            <option value="25+">25+ editors</option>
                          </select>
                        </div>
                      </div>
                      <div>
                        <label className="text-sm text-gray-400 mb-1 block">
                          How can we help? *
                        </label>
                        <textarea
                          name="message"
                          value={formData.message}
                          onChange={handleChange}
                          placeholder="Tell us about your video production needs..."
                          required
                          rows={4}
                          className="w-full px-3 py-2 rounded-md bg-[#0a0f1a] border border-gray-700 text-white resize-none"
                        />
                      </div>
                      <Button
                        type="submit"
                        className="w-full bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold"
                      >
                        Send Message
                      </Button>
                    </form>
                  )}
                </CardContent>
              </Card>
            </motion.div>

            {/* Benefits & Contact Info */}
            <motion.div
              initial={{ opacity: 0, x: 20 }}
              animate={{ opacity: 1, x: 0 }}
              transition={{ duration: 0.5, delay: 0.2 }}
              className="space-y-6"
            >
              {/* Benefits */}
              <div className="space-y-4">
                <h3 className="text-lg font-semibold text-white mb-4">
                  Why Teams Choose SPLICE
                </h3>
                {benefits.map((benefit) => (
                  <div
                    key={benefit.title}
                    className="flex items-start gap-4 p-4 rounded-lg bg-[#0d1420] border border-gray-800"
                  >
                    <div className="w-10 h-10 rounded-lg bg-[#00D4FF]/10 flex items-center justify-center shrink-0">
                      <benefit.icon className="w-5 h-5 text-[#00D4FF]" />
                    </div>
                    <div>
                      <h4 className="font-medium text-white">{benefit.title}</h4>
                      <p className="text-sm text-gray-400">{benefit.description}</p>
                    </div>
                  </div>
                ))}
              </div>

              {/* Direct Contact */}
              <Card className="bg-[#0d1420] border-gray-800">
                <CardContent className="p-6">
                  <h3 className="text-lg font-semibold text-white mb-4">
                    Prefer to reach out directly?
                  </h3>
                  <div className="space-y-3">
                    <a
                      href="mailto:sales@splice.app"
                      className="flex items-center gap-3 text-gray-400 hover:text-[#00D4FF] transition-colors"
                    >
                      <Mail className="w-5 h-5" />
                      <span>sales@splice.app</span>
                    </a>
                    <a
                      href="https://discord.gg/splice"
                      target="_blank"
                      rel="noopener noreferrer"
                      className="flex items-center gap-3 text-gray-400 hover:text-[#00D4FF] transition-colors"
                    >
                      <MessageCircle className="w-5 h-5" />
                      <span>Join our Discord</span>
                    </a>
                  </div>
                </CardContent>
              </Card>

              {/* Quick CTA */}
              <div className="bg-gradient-to-r from-[#00D4FF]/10 to-purple-500/10 border border-[#00D4FF]/20 rounded-lg p-6">
                <h3 className="text-lg font-semibold text-white mb-2">
                  Just want to try it out?
                </h3>
                <p className="text-sm text-gray-400 mb-4">
                  Start with our 14-day free trial. No credit card required.
                </p>
                <Button
                  className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold"
                  asChild
                >
                  <Link href="/pricing">View Pricing</Link>
                </Button>
              </div>
            </motion.div>
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}
