"use client";

import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Play, Zap, Clock, Sparkles, Star, Shield, Users, X } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import Link from "next/link";
import { useState } from "react";

// Pre-generated waveform data to avoid hydration mismatch from Math.random()
const WAVEFORM_BARS = [
  { h: 45, o: 1 }, { h: 72, o: 0.5 }, { h: 38, o: 1 }, { h: 85, o: 0.5 }, { h: 52, o: 1 },
  { h: 28, o: 0.5 }, { h: 67, o: 1 }, { h: 43, o: 0.5 }, { h: 91, o: 1 }, { h: 35, o: 0.5 },
  { h: 78, o: 1 }, { h: 55, o: 0.5 }, { h: 32, o: 1 }, { h: 88, o: 0.5 }, { h: 48, o: 1 },
  { h: 25, o: 0.5 }, { h: 73, o: 1 }, { h: 62, o: 0.5 }, { h: 95, o: 1 }, { h: 40, o: 0.5 },
  { h: 58, o: 1 }, { h: 82, o: 0.5 }, { h: 36, o: 1 }, { h: 69, o: 0.5 }, { h: 50, o: 1 },
  { h: 29, o: 0.5 }, { h: 76, o: 1 }, { h: 44, o: 0.5 }, { h: 87, o: 1 }, { h: 33, o: 0.5 },
  { h: 65, o: 1 }, { h: 53, o: 0.5 }, { h: 92, o: 1 }, { h: 41, o: 0.5 }, { h: 70, o: 1 },
  { h: 27, o: 0.5 }, { h: 80, o: 1 }, { h: 57, o: 0.5 }, { h: 34, o: 1 }, { h: 83, o: 0.5 },
  { h: 46, o: 1 }, { h: 68, o: 0.5 }, { h: 39, o: 1 }, { h: 90, o: 0.5 }, { h: 54, o: 1 },
  { h: 31, o: 0.5 }, { h: 75, o: 1 }, { h: 60, o: 0.5 }, { h: 86, o: 1 }, { h: 42, o: 0.5 },
  { h: 63, o: 1 }, { h: 79, o: 0.5 }, { h: 37, o: 1 }, { h: 71, o: 0.5 }, { h: 49, o: 1 },
  { h: 26, o: 0.5 }, { h: 84, o: 1 }, { h: 56, o: 0.5 }, { h: 93, o: 1 }, { h: 47, o: 0.5 },
];

export function Hero() {
  const [showDemo, setShowDemo] = useState(false);

  return (
    <section className="relative min-h-screen flex items-center justify-center pt-20 pb-16 overflow-hidden dark">
      {/* Background gradient */}
      <div className="absolute inset-0 hero-gradient" />

      {/* Grid pattern */}
      <div className="absolute inset-0 grid-pattern opacity-50" />

      {/* Animated background elements - hidden on mobile for performance */}
      <div className="absolute inset-0 overflow-hidden hidden sm:block">
        <motion.div
          className="absolute top-1/4 left-1/4 w-[500px] h-[500px] bg-[#00D4FF]/10 rounded-full blur-[100px] will-change-transform"
          animate={{
            scale: [1, 1.2, 1],
            opacity: [0.3, 0.5, 0.3],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />
        <motion.div
          className="absolute bottom-1/4 right-1/4 w-[400px] h-[400px] bg-[#00A8CC]/10 rounded-full blur-[100px] will-change-transform"
          animate={{
            scale: [1.2, 1, 1.2],
            opacity: [0.5, 0.3, 0.5],
          }}
          transition={{
            duration: 8,
            repeat: Infinity,
            ease: "easeInOut",
          }}
        />
      </div>

      <div className="container mx-auto px-4 relative z-10">
        <div className="max-w-4xl mx-auto text-center">
          {/* Social Proof Badge */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="flex items-center justify-center gap-4 mb-6"
          >
            <Badge
              variant="outline"
              className="px-4 py-1.5 text-sm font-medium border-[#00D4FF]/50 bg-[#00D4FF]/10 text-[#00D4FF]"
            >
              <Sparkles className="w-3.5 h-3.5 mr-1.5" />
              Professional Beta - Save 40%
            </Badge>
            <div className="hidden sm:flex items-center gap-1 text-sm text-gray-400">
              <div className="flex -space-x-2">
                {[1, 2, 3, 4].map((i) => (
                  <div
                    key={i}
                    className="w-6 h-6 rounded-full bg-gradient-to-br from-[#00D4FF] to-[#0099CC] border-2 border-[#060b14] flex items-center justify-center text-[10px] font-bold text-black"
                  >
                    {String.fromCharCode(64 + i)}
                  </div>
                ))}
              </div>
              <span className="ml-2">
                <span className="text-white font-semibold">10,000+</span> Professional Editors & Studios
              </span>
            </div>
          </motion.div>

          {/* Main Headline */}
          <motion.h1
            className="text-4xl sm:text-5xl md:text-6xl lg:text-7xl font-bold tracking-tight mb-6 text-white"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
          >
            Cut Your Editing Time in Half.{" "}
            <span className="gradient-text">Automatically.</span>
          </motion.h1>

          {/* Subheadline - Pain point focused */}
          {/* Subheadline - Pain point focused */}
          <motion.p
            className="text-lg sm:text-xl text-gray-400 mb-8 max-w-2xl mx-auto"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.2 }}
          >
            Studio-grade AI that cuts silence, isolates vocals, and generates music in seconds.
            Stop editing waveforms and start telling stories.
          </motion.p>

          {/* Star Rating */}
          <motion.div
            className="flex items-center justify-center gap-2 mb-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.25 }}
          >
            <div className="flex items-center gap-0.5">
              {[1, 2, 3, 4, 5].map((i) => (
                <Star key={i} className="w-5 h-5 fill-yellow-400 text-yellow-400" />
              ))}
            </div>
            <span className="text-sm text-gray-400">
              <span className="text-white font-semibold">4.9/5</span> from verified pros
            </span>
          </motion.div>

          {/* Benefit bullets */}
          <motion.div
            className="flex flex-wrap justify-center gap-6 mb-10"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.3 }}
          >
            <div className="flex items-center gap-2 text-sm text-gray-300">
              <Clock className="w-4 h-4 text-[#00D4FF]" />
              <span>Reduce cut time by 90%</span>
            </div>
            <div className="flex items-center gap-2 text-sm text-gray-300">
              <Zap className="w-4 h-4 text-[#00D4FF]" />
              <span>Native UXP Performance</span>
            </div>
            <div className="flex items-center gap-2 text-sm text-gray-300">
              <Sparkles className="w-4 h-4 text-[#00D4FF]" />
              <span>Studio-Quality Results</span>
            </div>
          </motion.div>

          {/* CTAs */}
          <motion.div
            className="flex flex-col sm:flex-row items-center justify-center gap-4 mb-8"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.4 }}
          >
            <Button
              size="lg"
              className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold text-lg px-8 h-14 btn-glow min-w-[200px]"
              asChild
            >
              <Link href="#pricing">
                Start Free Trial
                <span className="ml-2 text-xs font-normal opacity-75 hidden sm:inline-block">No credit card required</span>
              </Link>
            </Button>
            <Button
              size="lg"
              variant="ghost"
              className="text-gray-400 hover:text-white hover:bg-white/5"
              onClick={() => setShowDemo(true)}
            >
              <Play className="w-4 h-4 mr-2" />
              Watch Demo
            </Button>
          </motion.div>

          {/* Trust indicators */}
          <motion.div
            className="flex items-center justify-center gap-6 text-xs text-gray-500 mb-16"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 0.5, delay: 0.5 }}
          >
            <div className="flex items-center gap-1.5">
              <Shield className="w-4 h-4 text-green-500" />
              <span>14-day free trial</span>
            </div>
            <div className="flex items-center gap-1.5">
              <Users className="w-4 h-4" />
              <span>Cancel anytime</span>
            </div>
            <div className="flex items-center gap-1.5">
              <Zap className="w-4 h-4" />
              <span>Setup in 2 min</span>
            </div>
          </motion.div>

          {/* Visual Dashboard Mockup */}
          <motion.div
            className="relative"
            initial={{ opacity: 0, y: 40 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.7, delay: 0.6 }}
          >
            {/* Main Dashboard Container */}
            <div className="relative rounded-xl overflow-hidden shadow-2xl border border-[#00D4FF]/20 bg-[#0d1420] aspect-[16/9] max-w-5xl mx-auto group">

              {/* Abstract Header */}
              <div className="absolute inset-x-0 top-0 h-10 sm:h-12 bg-[#1a2332] border-b border-gray-800 flex items-center px-4 gap-2 z-10">
                <div className="flex gap-1.5">
                  <div className="w-2.5 h-2.5 rounded-full bg-red-500/20 border border-red-500/30" />
                  <div className="w-2.5 h-2.5 rounded-full bg-yellow-500/20 border border-yellow-500/30" />
                  <div className="w-2.5 h-2.5 rounded-full bg-green-500/20 border border-green-500/30" />
                </div>
                <div className="ml-4 h-5 sm:h-6 w-1/4 sm:w-1/3 bg-gray-800/50 rounded-md border border-gray-700/30" />
              </div>

              {/* Abstract Sidebar (Desktop) */}
              <div className="absolute top-12 bottom-0 left-0 w-16 sm:w-64 bg-[#131b29] border-r border-gray-800 hidden sm:flex flex-col gap-4 p-4 z-10">
                <div className="h-8 w-full bg-[#00D4FF]/5 rounded-lg border border-[#00D4FF]/10" />
                <div className="space-y-2">
                  <div className="h-6 w-3/4 bg-gray-800/30 rounded" />
                  <div className="h-6 w-full bg-gray-800/30 rounded" />
                  <div className="h-6 w-5/6 bg-gray-800/30 rounded" />
                </div>
              </div>

              {/* Main Content Area */}
              <div className="absolute top-10 sm:top-12 inset-0 sm:left-64 p-4 sm:p-6 overflow-hidden bg-[#060b14]/50">
                {/* Timeline Header */}
                <div className="flex justify-between items-center mb-6">
                  <div className="h-8 w-1/3 bg-gray-800/50 rounded" />
                  <div className="h-8 w-24 bg-[#00D4FF] rounded shadow-[0_0_15px_rgba(0,212,255,0.3)]" />
                </div>

                {/* Audio Waveforms */}
                <div className="space-y-4">
                  <div className="relative h-20 sm:h-24 w-full bg-gray-900/80 rounded-lg border border-gray-800 overflow-hidden">
                    {/* Mock Waveform Pattern */}
                    <div className="absolute inset-0 flex items-center gap-0.5 px-2 opacity-30">
                      {WAVEFORM_BARS.map((bar, i) => (
                        <div
                          key={i}
                          className="flex-1 bg-[#00D4FF]"
                          style={{ height: `${bar.h}%`, opacity: bar.o }}
                        />
                      ))}
                    </div>
                    {/* Selection Box */}
                    <div className="absolute top-0 bottom-0 left-1/4 w-1/3 bg-[#00D4FF]/10 border sm:border-x border-[#00D4FF]/30 flex items-center justify-center">
                      <span className="text-[10px] text-[#00D4FF] font-mono tracking-widest hidden sm:inline-block">REMOVING SILENCE...</span>
                    </div>
                  </div>
                  <div className="h-20 sm:h-24 w-full bg-gray-900/50 rounded-lg border border-gray-800 opacity-50" />
                  <div className="h-20 sm:h-24 w-full bg-gray-900/50 rounded-lg border border-gray-800 opacity-30" />
                </div>
              </div>

              {/* Glow Overlay */}
              <div className="absolute inset-0 bg-gradient-to-t from-[#00D4FF]/5 via-transparent to-transparent pointer-events-none" />
            </div>

            {/* Floating UI mockup - Left side (desktop only for performance) */}
            <motion.div
              className="absolute left-0 top-[40%] -ml-6 bg-[#0d1829]/95 backdrop-blur-md rounded-xl p-4 shadow-2xl border border-[#00D4FF]/20 hidden lg:block z-20 will-change-transform"
              animate={{ y: [0, -10, 0] }}
              transition={{ duration: 5, repeat: Infinity, ease: "easeInOut" }}
            >
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 rounded-full bg-green-500/20 flex items-center justify-center border border-green-500/20">
                  <Clock className="w-6 h-6 text-green-400" />
                </div>
                <div>
                  <p className="font-medium text-sm text-white">Time Saved</p>
                  <p className="text-lg font-bold text-green-400">8h 23m</p>
                </div>
              </div>
            </motion.div>

            {/* Floating UI mockup - Right side (desktop only for performance) */}
            <motion.div
              className="absolute right-0 bottom-8 -mr-6 bg-[#0d1829]/95 backdrop-blur-md rounded-xl p-4 shadow-2xl border border-[#00D4FF]/20 hidden lg:block z-20 will-change-transform"
              animate={{ y: [0, -10, 0] }}
              transition={{ duration: 4, repeat: Infinity, ease: "easeInOut", delay: 1 }}
            >
              <div className="flex items-center gap-4">
                <div className="w-12 h-12 rounded-full bg-[#00D4FF]/20 flex items-center justify-center border border-[#00D4FF]/20">
                  <Zap className="w-6 h-6 text-[#00D4FF]" />
                </div>
                <div>
                  <p className="font-medium text-sm text-white">Silences Removed</p>
                  <p className="text-lg font-bold text-gray-200">247 cuts</p>
                </div>
              </div>
            </motion.div>
          </motion.div>


        </div>
      </div>

      {/* Demo Video Modal */}
      <AnimatePresence>
        {showDemo && (
          <motion.div
            className="fixed inset-0 z-50 flex items-center justify-center p-4 bg-black/80 backdrop-blur-sm"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={() => setShowDemo(false)}
          >
            <motion.div
              className="relative w-full max-w-4xl bg-[#0d1829] rounded-2xl overflow-hidden shadow-2xl border border-[#00D4FF]/20"
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              {/* Close button */}
              <button
                onClick={() => setShowDemo(false)}
                className="absolute top-4 right-4 z-10 p-2 rounded-full bg-black/50 text-white hover:bg-black/70 transition-colors"
              >
                <X className="w-5 h-5" />
              </button>

              {/* Video container with placeholder */}
              <div className="aspect-video bg-gradient-to-br from-[#0d1829] to-[#1a2744] flex flex-col items-center justify-center p-8">
                {/* TODO: Replace with actual video embed once recorded */}
                {/* <video src="/videos/demo.mp4" controls autoPlay className="w-full h-full" /> */}

                {/* Placeholder while video is being created */}
                <div className="text-center">
                  <div className="w-20 h-20 mx-auto mb-6 rounded-full bg-[#00D4FF]/20 flex items-center justify-center">
                    <Play className="w-10 h-10 text-[#00D4FF]" />
                  </div>
                  <h3 className="text-2xl font-bold text-white mb-2">Demo Coming Soon</h3>
                  <p className="text-gray-400 mb-6 max-w-md">
                    We&apos;re recording an awesome demo video. In the meantime, start your free trial to see SPLICE in action!
                  </p>
                  <Button
                    className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold"
                    onClick={() => {
                      setShowDemo(false);
                      document.getElementById("pricing")?.scrollIntoView({ behavior: "smooth" });
                    }}
                  >
                    Start Free Trial
                  </Button>
                </div>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </section>
  );
}

