   201→// ============================================================================
   202→// JSX CONNECTION CHECK
   203→// ============================================================================
   204→async function checkJSXConnection() {
   205→    try {
   206→        const result = await jsx.call('getVersion');
   207→        console.log('[SPLICE] JSX connection OK, version:', result);
   208→        return true;
   209→    } catch (e) {
   210→        console.warn('[SPLICE] JSX connection failed:', e.message);
   211→        setStatus('Premiere Pro connection failed', true);
   212→        return false;
   213→    }
   214→}
   215→
   216→async function checkSequenceOpen() {
   217→    try {
   218→        const result = await jsx.call('checkSequenceOpen');
   219→        return result === true || result === 'true';
   220→    } catch {
   221→        return false;
   222→    }
   223→}
