   225→// ============================================================================
   226→// OPTIONS TOGGLE
   227→// ============================================================================
   228→function initOptionsToggle() {
   229→    console.log('[SPLICE] initOptionsToggle - toggle:', !!ui.optionsToggle, 'panel:', !!ui.optionsPanel);
   230→    if (ui.optionsToggle && ui.optionsPanel) {
   231→        ui.optionsToggle.addEventListener('click', () => {
   232→            console.log('[SPLICE] Options toggle clicked');
   233→            // Check current state BEFORE toggling
   234→            const wasCollapsed = ui.optionsPanel.classList.contains('collapsed');
   235→            ui.optionsPanel.classList.toggle('collapsed');
   236→            // If it was collapsed, it's now expanded (show 'expanded' class)
   237→            ui.optionsToggle.classList.toggle('expanded', wasCollapsed);
   238→            saveSettings({ expandedOptions: wasCollapsed });
   239→        });
   240→
   241→        // Restore state
   242→        const settings = getSettings();
   243→        if (!settings.expandedOptions) {
   244→            ui.optionsPanel.classList.add('collapsed');
   245→            ui.optionsToggle.classList.remove('expanded');
   246→        } else {
   247→            ui.optionsToggle.classList.add('expanded');
   248→            ui.optionsPanel.classList.remove('collapsed');
   249→        }
   250→    }
   251→}
