   256→function initTextEditorToggle() {
   257→    const toggle = document.getElementById('textEditorToggle');
   258→    const panel = document.getElementById('text-editor-panel');
   259→    console.log('[SPLICE] initTextEditorToggle - toggle:', !!toggle, 'panel:', !!panel);
   260→
   261→    if (toggle && panel) {
   262→        toggle.addEventListener('click', (e) => {
   263→            e.preventDefault();
   264→            e.stopPropagation();
   265→            console.log('[SPLICE] Text Editor toggle clicked');
   266→            setStatus('Text Editor: ' + (panel.classList.contains('collapsed') ? 'Opening' : 'Closing'));
   267→            // Check state BEFORE toggling
   268→            const wasCollapsed = panel.classList.contains('collapsed');
   269→            panel.classList.toggle('collapsed');
   270→            const icon = toggle.querySelector('.toggle-icon');
   271→            console.log('[SPLICE] wasCollapsed:', wasCollapsed, 'icon:', !!icon);
   272→            if (icon) {
   273→                // If it was collapsed, it's now expanded (show -)
   274→                icon.textContent = wasCollapsed ? '-' : '+';
   275→            }
   276→        });
   277→    } else {
   278→        console.error('[SPLICE] Text Editor toggle elements not found!');
   279→    }
