   403→    }
   404→
   405→    // Filler word detection toggle
   406→    if (ui.enableFillerDetection && ui.fillerSettings) {
   407→        ui.enableFillerDetection.addEventListener('change', () => {
   408→            ui.fillerSettings.classList.toggle('collapsed', !ui.enableFillerDetection.checked);
   409→            saveSettings({ enableFillerDetection: ui.enableFillerDetection.checked });
