   414→// ============================================================================
   415→// PRESET SELECTOR
   416→// ============================================================================
   417→// PRESETS is defined in config.js
   418→
   419→function initPresetSelector() {
   420→    if (!ui.presetSelector) return;
   421→
   422→    ui.presetSelector.addEventListener('change', () => {
   423→        const presetId = ui.presetSelector.value;
   424→        applyPreset(presetId);
   425→    });
   426→
   427→    // Load current preset
   428→    const settings = getSettings();
   429→    if (settings.activePreset) {
   430→        ui.presetSelector.value = settings.activePreset;
   431→    }
   432→}
   433→
   434→function applyPreset(presetId) {
   435→    const preset = PRESETS[presetId];
   436→    if (!preset) return;
   437→
   438→    // Custom preset - keep current settings
   439→    if (presetId === 'custom' || !preset.settings) {
   440→        saveSettings({ activePreset: 'custom' });
   441→        setStatus('Using custom settings');
   442→        return;
   443→    }
   444→
   445→    const settings = preset.settings;
   446→
   447→    // Apply to UI
   448→    if (ui.sensitivitySlider && settings.sensitivity !== undefined) {
   449→        ui.sensitivitySlider.value = settings.sensitivity;
   450→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   451→    }
   452→
   453→    if (ui.enableJCut) {
   454→        ui.enableJCut.checked = settings.enableJCut || false;
   455→        if (ui.jcutSettings) {
   456→            ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   457→        }
   458→    }
   459→
   460→    if (settings.jCutLeadIn !== undefined && ui.jcutLeadIn) {
   461→        ui.jcutLeadIn.value = Math.round(settings.jCutLeadIn * 100);
   462→        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jCutLeadIn + 's';
   463→    }
   464→
   465→    if (settings.jCutLeadOut !== undefined && ui.jcutLeadOut) {
   466→        ui.jcutLeadOut.value = Math.round(settings.jCutLeadOut * 100);
   467→        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jCutLeadOut + 's';
   468→    }
   469→
   470→    // Save merged settings
   471→    saveSettings({
   472→        activePreset: presetId,
   473→        sensitivity: settings.sensitivity,
   474→        threshold: settings.threshold,
   475→        minSilenceLength: settings.minSilenceLength,
   476→        paddingStart: settings.paddingStart,
   477→        paddingEnd: settings.paddingEnd,
   478→        enableTakesDetection: settings.enableTakesDetection,
   479→        enableJCut: settings.enableJCut || false,
   480→        jcutLeadIn: settings.jCutLeadIn || 0.3,
   481→        jcutLeadOut: settings.jCutLeadOut || 0.2
   482→    });
   483→
   484→    setStatus(`Applied ${preset.name} preset`);
   485→}
   486→
   487→// ============================================================================
   488→// LOAD SETTINGS TO UI
   489→// ============================================================================
   490→function loadSettingsToUI() {
   491→    const settings = getSettings();
   492→
   493→    if (ui.sensitivitySlider) {
   494→        ui.sensitivitySlider.value = settings.sensitivity;
   495→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   496→    }
   497→
   498→    if (ui.enableTakesDetection) ui.enableTakesDetection.checked = settings.enableTakesDetection;
   499→    if (ui.enableJCut) ui.enableJCut.checked = settings.enableJCut;
   500→    if (ui.enableZoom) ui.enableZoom.checked = settings.enableZoom;
   501→    if (ui.enableChapters) ui.enableChapters.checked = settings.enableChapters;
   502→
   503→    if (ui.jcutSettings) ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   504→    if (ui.zoomSettings) ui.zoomSettings.classList.toggle('collapsed', !settings.enableZoom);
   505→    if (ui.chapterSettings) ui.chapterSettings.classList.toggle('collapsed', !settings.enableChapters);
   506→
   507→    if (ui.jcutLeadIn) {
   508→        ui.jcutLeadIn.value = Math.round(settings.jcutLeadIn * 100);
   509→        if (ui.jcutLeadInValue) ui.jcutLeadInValue.textContent = settings.jcutLeadIn + 's';
   510→    }
   511→    if (ui.jcutLeadOut) {
   512→        ui.jcutLeadOut.value = Math.round(settings.jcutLeadOut * 100);
   513→        if (ui.jcutLeadOutValue) ui.jcutLeadOutValue.textContent = settings.jcutLeadOut + 's';
   514→    }
   515→
   516→    if (ui.presetSelector && settings.activePreset) {
   517→        ui.presetSelector.value = settings.activePreset;
   518→    }
   519→}
