   647→                        window.currentTranscript = analyzeData.transcript;
   648→                        window.spliceState.lastTranscript = analyzeData.transcript;
   649→                    }
   650→                } else {
   651→                    // Handle specific error codes
   652→                    const errorMsg = await parseErrorResponse(analyzeResponse);
   653→                    if (analyzeResponse.status === 402) {
   654→                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   655→                        setStatus('Takes detection skipped: insufficient credits', true);
   656→                    } else if (analyzeResponse.status === 401) {
   657→                        console.warn('[SPLICE] Takes detection skipped: authentication required');
