   696→        }
   697→
   698→        // Detect chapters if enabled
   699→        if (settings.enableChapters) {
   700→            updateProgress(80, 'Detecting chapters...');
   701→            try {
   702→                const chapterResponse = await fetchWithTimeout(
   703→                    `${getBackendUrl()}/chapters`,
