   902→async function seekToTime(seconds) {
   903→    try {
   904→        // Check if there's an active sequence before seeking
   905→        if (!app?.project?.activeSequence) {
   906→            console.warn('[SPLICE] No active sequence to seek in');
   907→            return;
   908→        }
   909→        // Use JSX to set playhead position
   910→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
   911→        const ticks = Math.round(seconds * 254016000000);
   912→        await jsx.evalScript(`app.project.activeSequence.setPlayerPosition(${ticks})`);
   913→    } catch (e) {
   914→        console.warn('[SPLICE] Seek failed:', e);
   915→    }
   916→}
