   110→<body>
   111→  <h1>SPLICE</h1>
   112→  <p id="status">Plugin loaded!</p>
   113→  <button id="readClipsBtn">Read Timeline Clips</button>
   114→  <button id="exportAudioBtn">Export Audio (Test)</button>
   115→  <button id="analyzeBtn" class="analyze-btn">Analyze Audio</button>
   116→  <div id="clips"></div>
   117→
   118→  <div class="results-section" id="resultsSection" style="display: none;">
   119→    <h2>Transcript</h2>
   120→    <div id="transcript"></div>
   121→    <h2>Detected Takes</h2>
   122→    <div id="takes"></div>
   123→  </div>
   124→
   125→  <script>
   126→    const ppro = require('premierepro');
   127→
   128→    document.getElementById('readClipsBtn').addEventListener('click', async () => {
   129→      const statusEl = document.getElementById('status');
   130→      const clipsEl = document.getElementById('clips');
   131→      const btn = document.getElementById('readClipsBtn');
   132→
   133→      btn.disabled = true;
   134→      statusEl.textContent = 'Reading clips...';
   135→      clipsEl.innerHTML = '';
   136→
   137→      try {
   138→        const project = await ppro.Project.getActiveProject();
   139→        if (!project) {
   140→          statusEl.textContent = 'No project open';
   141→          btn.disabled = false;
   142→          return;
   143→        }
   144→
   145→        const sequence = await project.getActiveSequence();
   146→        if (!sequence) {
   147→          statusEl.textContent = 'No active sequence';
   148→          btn.disabled = false;
   149→          return;
   150→        }
   151→
   152→        const clipNames = [];
   153→        const numTracks = await sequence.getVideoTrackCount();
   154→
   155→        for (let i = 0; i < numTracks; i++) {
   156→          const track = await sequence.getVideoTrack(i);
   157→          if (!track) continue;
   158→
   159→          const clips = await track.getTrackItems(ppro.Constants.TrackItemType.CLIP, false);
   160→          if (!clips) continue;
   161→
   162→          for (let j = 0; j < clips.length; j++) {
   163→            const clip = clips[j];
   164→            if (!clip) continue;
   165→            const name = await clip.getName();
   166→            clipNames.push(name);
   167→          }
   168→        }
   169→
   170→        if (clipNames.length === 0) {
   171→          clipsEl.innerHTML = '<div class="no-clips">No clips in timeline</div>';
   172→          statusEl.textContent = 'No clips found';
   173→        } else {
   174→          clipsEl.innerHTML = clipNames
   175→            .map(name => `<div class="clip-item">${name}</div>`)
   176→            .join('');
   177→          statusEl.textContent = `Found ${clipNames.length} clip(s)`;
   178→        }
   179→      } catch (err) {
   180→        statusEl.textContent = `Error: ${err.message}`;
   181→        console.error('SPLICE error:', err);
   182→      }
   183→
   184→      btn.disabled = false;
   185→    });
   186→
   187→    // Export Audio functionality
   188→    // WAV 48kHz 16-bit preset path
   189→    const WAV_PRESET_PATH = '/Applications/Adobe Premiere Pro (Beta)/Adobe Premiere Pro (Beta).app/Contents/Settings/EncoderPresets/Wave48mono16.epr';
   190→
   191→    document.getElementById('exportAudioBtn').addEventListener('click', async () => {
   192→      const statusEl = document.getElementById('status');
   193→      const btn = document.getElementById('exportAudioBtn');
   194→
   195→      btn.disabled = true;
   196→      statusEl.textContent = 'Preparing audio export...';
   197→
   198→      try {
   199→        const project = await ppro.Project.getActiveProject();
   200→        if (!project) {
   201→          statusEl.textContent = 'No project open';
   202→          btn.disabled = false;
   203→          return;
   204→        }
   205→
   206→        const sequence = await project.getActiveSequence();
   207→        if (!sequence) {
   208→          statusEl.textContent = 'No active sequence';
   209→          btn.disabled = false;
   210→          return;
   211→        }
   212→
   213→        // Get EncoderManager
   214→        const encoderManager = ppro.EncoderManager.getManager();
   215→        if (!encoderManager) {
   216→          statusEl.textContent = 'EncoderManager not available';
   217→          btn.disabled = false;
   218→          return;
   219→        }
   220→
   221→        // Use fixed temp path for now (file picker permission issue)
   222→        const outputPath = '/tmp/splice_audio_export.wav';
   223→        statusEl.textContent = `Exporting to: ${outputPath}`;
   224→
   225→        // Export with WAV preset
   226→        const result = await encoderManager.exportSequence(
   227→          sequence,
   228→          ppro.EncoderManager.EXPORT_IMMEDIATELY,
   229→          outputPath,
   230→          WAV_PRESET_PATH,
   231→          true // exportFull - export entire sequence
   232→        );
   233→
   234→        if (result) {
   235→          statusEl.textContent = `Export complete: ${outputPath}`;
   236→        } else {
   237→          statusEl.textContent = 'Export returned false - check console';
   238→        }
   239→
   240→      } catch (err) {
   241→        statusEl.textContent = `Export error: ${err.message}`;
   242→        console.error('SPLICE export error:', err);
   243→        console.error('Full error:', JSON.stringify(err, null, 2));
   244→      }
   245→
   246→      btn.disabled = false;
   247→    });
   248→
   249→    // Analyze Audio - call backend
   250→    const BACKEND_URL = 'http://127.0.0.1:3847';
   251→    const WAV_PATH = '/tmp/splice_audio_export.wav';
   252→
   253→    document.getElementById('analyzeBtn').addEventListener('click', async () => {
   254→      const statusEl = document.getElementById('status');
   255→      const btn = document.getElementById('analyzeBtn');
   256→      const resultsSection = document.getElementById('resultsSection');
   257→      const transcriptEl = document.getElementById('transcript');
   258→      const takesEl = document.getElementById('takes');
   259→
   260→      btn.disabled = true;
   261→      statusEl.textContent = 'Analyzing audio...';
   262→      resultsSection.style.display = 'none';
   263→
   264→      try {
   265→        const response = await fetch(`${BACKEND_URL}/analyze`, {
   266→          method: 'POST',
   267→          headers: { 'Content-Type': 'application/json' },
   268→          body: JSON.stringify({ wavPath: WAV_PATH })
   269→        });
   270→
   271→        if (!response.ok) {
   272→          const errorData = await response.json();
   273→          throw new Error(errorData.error || `HTTP ${response.status}`);
   274→        }
   275→
   276→        const data = await response.json();
   277→
   278→        if (!data.success) {
   279→          throw new Error(data.error || 'Analysis failed');
   280→        }
   281→
   282→        // Display transcript
   283→        transcriptEl.textContent = data.transcript.text || 'No transcript available';
   284→
   285→        // Display takes
   286→        if (data.takes && data.takes.takes && data.takes.takes.length > 0) {
   287→          takesEl.innerHTML = data.takes.takes.map((take, i) => {
   288→            const isBest = take.is_best ? ' best' : '';
   289→            const bestLabel = take.is_best ? ' (BEST)' : '';
   290→            return `
   291→              <div class="take-item${isBest}">
   292→                <div class="take-label">Take ${take.take_number}${bestLabel}</div>
   293→                <div class="take-time">${formatTime(take.start_time)} - ${formatTime(take.end_time)}</div>
   294→                <div class="take-text">${take.text}</div>
   295→              </div>
   296→            `;
   297→          }).join('');
   298→        } else {
   299→          takesEl.innerHTML = '<div class="no-clips">No takes detected</div>';
   300→        }
   301→
   302→        resultsSection.style.display = 'block';
   303→        statusEl.textContent = `Analysis complete: ${data.takes.takes.length} take(s) found`;
   304→
   305→      } catch (err) {
   306→        statusEl.textContent = `Analysis error: ${err.message}`;
   307→        console.error('SPLICE analysis error:', err);
   308→      }
   309→
   310→      btn.disabled = false;
   311→    });
   312→
   313→    function formatTime(seconds) {
   314→      const mins = Math.floor(seconds / 60);
   315→      const secs = Math.floor(seconds % 60);
   316→      return `${mins}:${secs.toString().padStart(2, '0')}`;
   317→    }
   318→  </script>
