   268→// ============================================================================
   269→// JSX CONNECTION CHECK
   270→// ============================================================================
   271→async function checkJSXConnection() {
   272→    try {
   273→        const result = await jsx.call('getVersion');
   274→        console.log('[SPLICE] JSX connection OK, version:', result);
   275→        return true;
   276→    } catch (e) {
