   283→/**
   284→ * Check if a sequence is currently open in the Timeline
   285→ * Enhanced with logging and retry logic
   286→ * @param {number} retries - Number of retry attempts (default: 2)
   287→ * @param {number} delayMs - Delay between retries in ms (default: 300)
   288→ * @returns {Promise<boolean>} true if sequence is open
   289→ */
   290→async function checkSequenceOpen(retries = 2, delayMs = 300) {
   291→    for (let attempt = 0; attempt <= retries; attempt++) {
   292→        try {
   293→            const result = await jsx.call('checkSequenceOpen');
   294→            console.log('[SPLICE] checkSequenceOpen attempt', attempt + 1, 'result:', result, 'type:', typeof result);
   295→
   296→            if (result === true || result === 'true') {
   297→                return true;
   298→            }
   299→
   300→            // If false, wait and retry (sequence might be loading)
   301→            if (attempt < retries) {
   302→                console.log('[SPLICE] checkSequenceOpen: No sequence found, retrying in', delayMs, 'ms...');
   303→                await new Promise(r => setTimeout(r, delayMs));
   304→            }
   305→        } catch (error) {
   306→            console.error('[SPLICE] checkSequenceOpen attempt', attempt + 1, 'failed:', error.message);
   307→
   308→            // On last attempt, return false but log the actual error
   309→            if (attempt === retries) {
   310→                console.error('[SPLICE] checkSequenceOpen: All attempts failed. Last error:', error.message);
   311→                // Store error for diagnostics
   312→                window.spliceState.lastSequenceCheckError = error.message;
   313→                return false;
   314→            }
   315→
   316→            // Wait before retry
   317→            await new Promise(r => setTimeout(r, delayMs));
   318→        }
   319→    }
   320→    return false;
   321→}
