   585→// ============================================================================
   586→// LOAD SETTINGS TO UI
   587→// ============================================================================
   588→function loadSettingsToUI() {
   589→    const settings = getSettings();
   590→
   591→    if (ui.sensitivitySlider) {
   592→        ui.sensitivitySlider.value = settings.sensitivity;
   593→        if (ui.sensitivityValue) ui.sensitivityValue.textContent = settings.sensitivity;
   594→    }
   595→
   596→    if (ui.enableTakesDetection) ui.enableTakesDetection.checked = settings.enableTakesDetection;
   597→    if (ui.enableJCut) ui.enableJCut.checked = settings.enableJCut;
   598→    if (ui.enableZoom) ui.enableZoom.checked = settings.enableZoom;
   599→    if (ui.enableChapters) ui.enableChapters.checked = settings.enableChapters;
   600→
   601→    if (ui.jcutSettings) ui.jcutSettings.classList.toggle('collapsed', !settings.enableJCut);
   602→    if (ui.zoomSettings) ui.zoomSettings.classList.toggle('collapsed', !settings.enableZoom);
