  1021→async function seekToTime(seconds) {
  1022→    try {
  1023→        // Check if there's an active sequence before seeking
  1024→        if (!app?.project?.activeSequence) {
  1025→            console.warn('[SPLICE] No active sequence to seek in');
  1026→            return;
  1027→        }
  1028→        // Use JSX to set playhead position
  1029→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
  1030→        const ticks = Math.round(seconds * 254016000000);
  1031→        await jsx.evalScript(`app.project.activeSequence.setPlayerPosition(${ticks})`);
  1032→    } catch (e) {
  1033→        console.warn('[SPLICE] Seek failed:', e);
  1034→    }
  1035→}
