  1150→async function buildSequence() {
  1151→    if (isOperationInProgress) return;
  1152→
  1153→    isOperationInProgress = true;
  1154→    setStatus('Building sequence...');
  1155→
  1156→    try {
  1157→        const settings = getSettings();
  1158→
  1159→        // Ensure we have sequence info
  1160→        if (!currentSequenceInfo) {
  1161→            currentSequenceInfo = await jsx.call('getActiveSequence');
  1162→        }
  1163→
  1164→        // Create cut list from selected silences
  1165→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1166→
  1167→        // Calculate total duration from sequence info or last silence
  1168→        let duration = 0;
  1169→        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1170→            duration = currentSequenceInfo.duration;
  1171→        } else if (previewSilences.length > 0) {
  1172→            const lastSilence = previewSilences[previewSilences.length - 1];
  1173→            duration = lastSilence.end + 10; // Add buffer
  1174→        }
  1175→
  1176→        // Call backend to generate cut list
  1177→        const cutListResponse = await fetchWithTimeout(
  1178→            `${getBackendUrl()}/cut-list`,
  1179→            {
  1180→                method: 'POST',
  1181→                headers: getAuthHeaders(),
  1182→                body: JSON.stringify({
  1183→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1184→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1185→                    duration: duration,
  1186→                    silences: selectedSilences,
  1187→                    takes: previewTakes,
  1188→                    enableTakes: settings.enableTakesDetection,
  1189→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1190→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1191→                })
  1192→            }
  1193→        );
  1194→
  1195→        if (!cutListResponse.ok) {
  1196→            throw new Error(await parseErrorResponse(cutListResponse));
  1197→        }
  1198→
  1199→        const cutList = await cutListResponse.json();
  1200→
  1201→        // Build sequence via JSX
  1202→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1203→
  1204→        if (result && result.success) {
  1205→            setStatus(`Built sequence: ${result.sequenceName}`);
  1206→            hidePreview();
  1207→        } else {
  1208→            throw new Error(result?.error || 'Build failed');
  1209→        }
  1210→    } catch (error) {
  1211→        setStatus('Build failed: ' + error.message, true);
  1212→    } finally {
  1213→        isOperationInProgress = false;
  1214→    }
  1215→}
  1216→
  1217→function copyYouTubeTimestamps() {
  1218→    if (currentChapters.length === 0) return;
  1219→
  1220→    const timestamps = currentChapters.map(ch =>
  1221→        `${formatTime(ch.startTime)} ${ch.title}`
  1222→    ).join('\n');
  1223→
  1224→    navigator.clipboard.writeText(timestamps).then(() => {
  1225→        setStatus('Copied YouTube timestamps');
  1226→    }).catch(() => {
  1227→        setStatus('Failed to copy', true);
  1228→    });
  1229→}
  1230→
  1231→async function addChapterMarkers() {
  1232→    if (isOperationInProgress || currentChapters.length === 0) return;
  1233→
  1234→    isOperationInProgress = true;
  1235→    setStatus('Adding chapter markers...');
  1236→
  1237→    try {
  1238→        for (const chapter of currentChapters) {
  1239→            await jsx.call('addChapterMarker', chapter.startTime, chapter.title, chapter.description);
  1240→        }
  1241→        setStatus(`Added ${currentChapters.length} chapter markers`);
  1242→    } catch (error) {
  1243→        setStatus('Failed to add markers: ' + error.message, true);
  1244→    } finally {
  1245→        isOperationInProgress = false;
  1246→    }
  1247→}
