  1261→            const comment = `Zoom ${zoom.intensity || 'medium'} - ${zoom.reason || 'emphasis'}`;
  1262→            await jsx.call('createMarker', zoom.time, 'SPLICE: Zoom', 0.5, comment, 3);
  1263→        }
