  1387→async function activateLicense() {
  1388→    if (!ui.licenseKeyInput) return;
  1389→
  1390→    const licenseKey = ui.licenseKeyInput.value.trim();
  1391→    if (!licenseKey) {
  1392→        setStatus('Please enter a license key', true);
  1393→        return;
  1394→    }
  1395→
  1396→    try {
  1397→        const response = await fetchWithTimeout(
  1398→            `${getBackendUrl()}/license/activate`,
  1399→            {
  1400→                method: 'POST',
  1401→                headers: { 'Content-Type': 'application/json' },
  1402→                body: JSON.stringify({ key: licenseKey })
  1403→            }
  1404→        );
  1405→
  1406→        if (!response.ok) {
  1407→            throw new Error(await parseErrorResponse(response));
  1408→        }
  1409→
  1410→        const data = await response.json();
  1411→        saveSettings({
  1412→            customerId: data.customerId,
  1413→            licenseKey: licenseKey
  1414→        });
  1415→
  1416→        if (ui.loginModal) ui.loginModal.classList.add('hidden');
  1417→        setStatus('License activated');
  1418→        updateCredits();
  1419→    } catch (error) {
  1420→        setStatus('Activation failed: ' + error.message, true);
  1421→    }
  1422→}
  1423→
  1424→/**
  1425→ * Look up license key by email address
  1426→ */
  1427→async function lookupLicense() {
  1428→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1429→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1430→
  1431→    if (!lookupEmailInput) return;
  1432→
  1433→    const email = lookupEmailInput.value.trim().toLowerCase();
  1434→    if (!email) {
  1435→        setStatus('Please enter your email address', true);
  1436→        return;
  1437→    }
  1438→
  1439→    // Basic email validation
  1440→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1441→    if (!emailRegex.test(email)) {
  1442→        setStatus('Please enter a valid email address', true);
  1443→        return;
  1444→    }
  1445→
  1446→    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1447→    if (lookupBtn) {
  1448→        lookupBtn.disabled = true;
  1449→        lookupBtn.textContent = 'Looking up...';
  1450→    }
  1451→
  1452→    try {
  1453→        const response = await fetchWithTimeout(
  1454→            `${getBackendUrl()}/license/lookup`,
  1455→            {
  1456→                method: 'POST',
  1457→                headers: { 'Content-Type': 'application/json' },
  1458→                body: JSON.stringify({ email })
  1459→            },
  1460→            30000
  1461→        );
  1462→
  1463→        const result = await response.json();
  1464→
  1465→        // Note: For security, the backend does NOT return the license key directly.
  1466→        // It sends the key via email to prevent enumeration attacks.
  1467→        if (result.success) {
  1468→            setStatus(result.message || 'If a license exists, it has been sent to your email.');
  1469→        } else {
  1470→            setStatus(result.error || 'Lookup failed. Please try again.', true);
  1471→        }
  1472→    } catch (error) {
  1473→        setStatus('Lookup failed: ' + error.message, true);
  1474→    } finally {
  1475→        if (lookupBtn) {
  1476→            lookupBtn.disabled = false;
  1477→            lookupBtn.textContent = 'Lookup';
  1478→        }
  1479→    }
  1480→}
