  1513→            ui.creditBadge.textContent = `${hours}h`;
  1514→            // Low warning when less than 1 hour remaining
  1515→            ui.creditBadge.className = data.hoursRemaining < 1 ? 'credit-badge low' : 'credit-badge ok';
  1516→            ui.creditBadge.style.display = 'flex';
  1517→        } else {
  1518→            ui.creditBadge.className = 'credit-badge error';
  1519→            ui.creditBadge.textContent = 'Error';
  1520→            ui.creditBadge.style.display = 'flex';
