    if (customUrl) {
        console.log('[SPLICE] Using custom backend URL from localStorage:', customUrl);
        return customUrl;
    }

    // Priority 2: Default to production backend
    // (CEP panels always run from file://, so can't reliably detect dev mode)
    console.log('[SPLICE] Using production backend URL:', SPLICE_CONFIG.BACKEND_PROD);
    return SPLICE_CONFIG.BACKEND_PROD;
}

// ============================================================================
// JSX BRIDGE - Communicate with Premiere Pro
// ============================================================================
const jsx = {
    cs: null,
    _initPromise: null,
    _initialized: false,

    /**
     * Initialize the CSInterface
     * Returns a promise that resolves when ready (true/false)
     * Does NOT reject - gracefully handles missing CEP environment
     */
    init: function() {
        if (this._initPromise) return this._initPromise;

        this._initPromise = new Promise((resolve) => {
            try {
                if (typeof CSInterface !== 'undefined') {
                    this.cs = new CSInterface();
                    // Verify we're in a real CEP environment by checking for __adobe_cep__
                    if (window.__adobe_cep__) {
                        this._initialized = true;
                        console.log('[JSX] CSInterface initialized successfully (CEP environment detected)');
                    } else {
                        // CSInterface exists but no CEP runtime - browser/dev mode
                        this._initialized = true;
                        console.log('[JSX] CSInterface initialized (development/browser mode)');
                    }
                } else {
                    console.warn('[JSX] CSInterface not available - panel may not function correctly');
                    this._initialized = false;
                }
            } catch (err) {
                console.error('[JSX] Failed to initialize CSInterface:', err);
                this._initialized = false;
            }
            resolve(this._initialized);
        });

        return this._initPromise;
    },

    /**
     * Ensure JSX is initialized before making calls
     */
    ensureInitialized: async function() {
        if (!this._initialized) {
            await this.init();
        }
        if (!this.cs) {
            throw new Error('CSInterface not initialized. Make sure you are running in CEP environment.');
        }
    },

    /**
     * Execute ExtendScript in Premiere Pro
     * @param {string} script - ExtendScript to execute
     * @returns {Promise<any>} - Parsed result
     */
    evalScript: async function(script) {
        await this.ensureInitialized();

        return new Promise((resolve, reject) => {
            this.cs.evalScript(script, (result) => {
                if (result === 'undefined' || result === undefined) {
                    resolve(null);
                    return;
                }

                // Check for EvalScript error or ExtendScript runtime errors
                if (typeof result === 'string' && (
                    result.indexOf('EvalScript error') !== -1 ||
                    result.indexOf('Error:') !== -1 ||
                    result.indexOf('TypeError:') !== -1 ||
                    result.indexOf('ReferenceError:') !== -1
                )) {
                    reject(new Error(result));
                    return;
                }

                // Try to parse JSON
                try {
                    const parsed = JSON.parse(result);
                    if (parsed.error) {
                        reject(new Error(parsed.error));
                    } else {
                        resolve(parsed);
                    }
                } catch (e) {
                    // Return as-is if not JSON
                    resolve(result);
                }
            });
        });
    },

    /**
     * Call a JSX function with arguments
     * @param {string} funcName - Function name
     * @param {...any} args - Arguments to pass
     * @returns {Promise<any>}
     */
    call: async function(funcName, ...args) {
        await this.ensureInitialized();

        const escapedArgs = args.map(arg => {
            if (arg === null || arg === undefined) return 'null';
            if (typeof arg === 'string') {
                // Escape backslashes first, then other special chars
                return `'${arg
                    .replace(/\\/g, '\\\\')
                    .replace(/'/g, "\\'")
                    .replace(/\r/g, '\\r')
                    .replace(/\n/g, '\\n')
                    .replace(/\t/g, '\\t')}'`;
            }
            if (typeof arg === 'object') {
                return `'${JSON.stringify(arg)
                    .replace(/\\/g, '\\\\')
                    .replace(/'/g, "\\'")}'`;
            }
            return String(arg);
        });

        const script = `${funcName}(${escapedArgs.join(', ')})`;
        return this.evalScript(script);
    }
};

// ============================================================================
// FETCH UTILITIES
// ============================================================================

/**
 * Fetch with timeout support
 * @param {string} url - URL to fetch
 * @param {object} options - Fetch options
 * @param {number} timeout - Timeout in ms
 */
async function fetchWithTimeout(url, options = {}, timeout = SPLICE_CONFIG.FETCH_TIMEOUT) {
    // DIAGNOSTIC: Log fetch request details before making request
    console.log('[SPLICE FETCH] === FETCH REQUEST START ===');
    console.log('[SPLICE FETCH] URL:', url);
    console.log('[SPLICE FETCH] Method:', options?.method || 'GET');
    console.log('[SPLICE FETCH] Headers:', options?.headers);
    console.log('[SPLICE FETCH] Timeout:', timeout);
    console.log('[SPLICE FETCH] Is CEP environment:', typeof CSInterface !== 'undefined');
    console.log('[SPLICE FETCH] =================================');

    const controller = new AbortController();
    const timeoutId = setTimeout(() => controller.abort(), timeout);

    try {
        const response = await fetch(url, {
            ...options,
            signal: controller.signal
        });
        clearTimeout(timeoutId);

        // DIAGNOSTIC: Log successful response
        console.log('[SPLICE FETCH] === FETCH RESPONSE SUCCESS ===');
        console.log('[SPLICE FETCH] Status:', response.status);
        console.log('[SPLICE FETCH] OK:', response.ok);
        console.log('[SPLICE FETCH] =================================');

        return response;
    } catch (error) {
        clearTimeout(timeoutId);

        // DIAGNOSTIC: Log raw fetch error before rethrowing
        console.log('[SPLICE FETCH] === FETCH ERROR CAUGHT ===');
        console.log('[SPLICE FETCH] Error name:', error?.name);
        console.log('[SPLICE FETCH] Error message:', error?.message);
        console.log('[SPLICE FETCH] Error type:', typeof error);
        console.log('[SPLICE FETCH] Error constructor:', error?.constructor?.name);
        console.log('[SPLICE FETCH] Is AbortError:', error.name === 'AbortError');
        console.log('[SPLICE FETCH] Is TypeError:', error.name === 'TypeError');
        console.log('[SPLICE FETCH] Full error object:', JSON.stringify(error, Object.getOwnPropertyNames(error)));
        console.log('[SPLICE FETCH] =================================');

        if (error.name === 'AbortError') {
            throw new Error(`Request timeout after ${timeout}ms`);
        }
        throw error;
    }
}

/**
 * Parse error response from backend
 * @param {Response} response - Fetch response
 */
async function parseErrorResponse(response) {
    try {
        const data = await response.json();
        return data.error || data.message || `HTTP ${response.status}`;
    } catch {
        return `HTTP ${response.status}: ${response.statusText}`;
    }
}

/**
 * Get auth headers for API requests
 */
function getAuthHeaders() {
    const headers = { 'Content-Type': 'application/json' };
    const settings = getSettings();

    if (settings.customerId) {
        headers['x-stripe-customer-id'] = settings.customerId;
    }
    if (settings.licenseKey) {
        headers['x-license-key'] = settings.licenseKey;
    }

    return headers;
}

